import React, { useContext, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, AuthContext } from './context/AuthContext';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Base from './pages/Base';
import Home from './pages/Home';
import Directories from './pages/Directories';
import './App.css';

// Protected route component that checks authentication and redirects if necessary
const ProtectedRoute = ({ component: Component }) => {
  const token = localStorage.getItem('token');
  const isAuthenticated = !!token;

  console.log('ProtectedRoute - Auth check: Token exists:', !!token);

  // Log detailed info for debugging
  useEffect(() => {
    const justLoggedIn = sessionStorage.getItem('justLoggedIn');
    if (justLoggedIn) {
      console.log('Protected route: User just logged in');
    }

    console.log('Protected route rendering for authenticated:', isAuthenticated);
  }, [isAuthenticated]);

  // If not authenticated, redirect to login
  if (!isAuthenticated) {
    console.log('ProtectedRoute - Not authenticated, redirecting to login');
    return <Navigate to="/login" />;
  }

  // Render the protected component
  return <Component />;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="app">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />

            {/* Base layout wrapper with nested routes */}
            <Route path="/" element={<Base />}>
              {/* Home route */}
              <Route index element={<Home />} />

              {/* Protected routes */}
              <Route path="/directories" element={<ProtectedRoute component={Directories} />} />

              {/* Redirect any other paths to home */}
              <Route path="*" element={<Navigate to="/" replace />} />
            </Route>
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
