import axios from 'axios';

// Make sure this points to your Django backend
const API_URL = 'http://localhost:8000';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  // Add withCredentials to handle CORS with credentials
  withCredentials: false
});

// Add request/response interceptors for better error handling
api.interceptors.request.use(
  (config) => {
    console.log('Sending request to:', config.url, 'with data:', config.data);
    const token = localStorage.getItem('token');
    if (token) {
      // Format token based on your backend requirements
      // Many backends expect "Bearer {token}" format
      config.headers.Authorization = `Bearer ${token}`;
      
      // Log token for debugging (don't include in production)
      console.log('Using token for request (first 10 chars):', token.substring(0, 10) + '...');
    } else {
      console.warn('No token found for authenticated request');
    }
    return config;
  },
  (error) => {
    console.error('API Request Error:', error);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API Response Error:', error);
    if (error.response) {
      console.error('Response data:', error.response.data);
      console.error('Response status:', error.response.status);
      console.error('Response headers:', error.response.headers);
      
      // Handle auth errors
      if (error.response.status === 401) {
        console.warn('Authentication error detected. Token may be invalid or expired.');
      }
    } else if (error.request) {
      console.error('No response received. Request:', error.request);
    }
    return Promise.reject(error);
  }
);

// Auth API calls
export const loginUser = async (credentials) => {
  try {
    console.log('Logging in with:', credentials);
    console.log('API URL:', API_URL);
    
    // Try using direct fetch for more control
    const response = await fetch(`${API_URL}/api/auth/login/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    });
    
    console.log('Login response status:', response.status);
    
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: 'Login failed' }));
      throw errorData;
    }
    
    const data = await response.json();
    console.log('Login response data:', data);
    
    // Store token right away for immediate use
    if (data.token) {
      console.log('Token received, storing in localStorage');
      localStorage.setItem('token', data.token);
      
      // Create a basic user if no user data provided
      if (!data.user) {
        const basicUser = { 
          email: credentials.email || 'user@example.com',
          isAuthenticated: true
        };
        data.user = basicUser;
        localStorage.setItem('user', JSON.stringify(basicUser));
      } else {
        localStorage.setItem('user', JSON.stringify(data.user));
      }
    } else {
      console.error('No token in response:', data);
    }
    
    return data;
  } catch (error) {
    console.error('Login error details:', error);
    throw { message: error.message || 'Login failed. Server may be unavailable.' };
  }
};

export const registerUser = async (userData) => {
  try {
    console.log('Registering with:', userData);
    
    // Try using fetch directly instead of axios
    const response = await fetch(`${API_URL}/api/auth/signup/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
      mode: 'cors', // Explicitly set CORS mode
    });
    
    console.log('Registration response status:', response.status);
    
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: 'Registration failed' }));
      throw errorData;
    }
    
    const data = await response.json();
    console.log('Registration response data:', data);
    
    // Store authentication data right away
    if (data.token) {
      localStorage.setItem('token', data.token);
    }
    
    if (data.user) {
      localStorage.setItem('user', JSON.stringify(data.user));
    } else if (userData.email) {
      // Create a basic user object if not provided by server
      const basicUser = {
        email: userData.email,
        fullName: userData.fullName,
        isAuthenticated: true
      };
      data.user = basicUser;
      localStorage.setItem('user', JSON.stringify(basicUser));
    }
    
    return data;
  } catch (error) {
    console.error('Registration error details:', error);
    throw error.message ? { message: error.message } : { message: 'Registration failed' };
  }
};

export const getUserProfile = async () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No authentication token found');
    }
    
    console.log('Getting user profile with token (first 10 chars):', token.substring(0, 10) + '...');
    
    // Use fetch directly for more control
    const response = await fetch(`${API_URL}/api/auth/profile/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      }
    });
    
    console.log('Profile fetch response status:', response.status);
    
    if (!response.ok) {
      if (response.status === 401) {
        console.error('Unauthorized: Token might be invalid or expired');
      }
      const errorData = await response.json().catch(() => ({ message: 'Profile fetch failed' }));
      throw errorData;
    }
    
    const data = await response.json();
    console.log('Profile data received:', data);
    
    return data;
  } catch (error) {
    console.error('Profile fetch error:', error);
    throw error.response?.data || { message: 'Failed to fetch profile' };
  }
};

// Chat API calls
export const sendChatMessage = (message) =>
  apiCall('/chat/', {
    method: 'POST',
    body: JSON.stringify({ message }),
  });

export const getChatHistory = () =>
  apiCall('/chat/history/', {
    method: 'GET',
  });

// Helper function for API calls
const apiCall = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');
  
  const defaultHeaders = {
    'Content-Type': 'application/json',
    ...(token && { Authorization: `Bearer ${token}` }),
  };

  const config = {
    ...options,
    headers: {
      ...defaultHeaders,
      ...options.headers,
    },
  };

  const response = await fetch(`${API_URL}${endpoint}`, config);
  
  if (!response.ok) {
    const error = await response.json();
    throw error;
  }

  return response.json();
};

export default api;
