import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { loginUser } from '../services/api';
import '../styles/Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    try {
      console.log('Attempting login with email:', email);
      
      // Store email in sessionStorage for use in Directories component
      sessionStorage.setItem('lastLoginEmail', email);
      
      // Use the loginUser function from api.js
      const response = await loginUser({ email, password });
      console.log('Login response received:', JSON.stringify(response));
      
      if (!response || !response.token) {
        console.error('Invalid login response:', response);
        throw new Error('Invalid login response - no token received');
      }
      
      // Store token immediately to ensure it's available for subsequent requests
      localStorage.setItem('token', response.token);
      console.log('Token stored in localStorage');
      
      // Ensure we pass email to the login function
      const loginData = {
        ...response,
        email: email // Add email to login data
      };
      
      // Use the login function from AuthContext
      await login(loginData);
      
      console.log('Login successful, redirecting directly to directories');
      
      // DIRECTLY go to directories - bypass all other redirects
      window.location.href = '/directories';
      
      // No need for timeouts or session flags that might conflict
      
    } catch (err) {
      console.error('Login error:', err);
      
      // Improved error handling with specific messages
      if (err.response && err.response.status === 401) {
        // Extract error message from response if available
        const errorMsg = err.response.data?.message || 'Invalid email or password';
        setError(errorMsg);
        
        // Clear password field for security
        setPassword('');
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check your internet connection.');
      } else {
        setError(err.message || 'Login failed. Please check your credentials.');
      }
    } finally {
      setLoading(false);
    }
  };
  
  // Check if already logged in
  React.useEffect(() => {
    // Check token to see if already logged in
    const token = localStorage.getItem('token');
    if (token) {
      console.log('Token found in localStorage, user already logged in');
      // If already logged in, go directly to directories
      window.location.href = '/directories';
    }
  }, []);

  return (
    <div className="login-container">
      <div className="login-form-wrapper">
        <h2>Login to Your Account</h2>
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          
          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        
        <div className="login-footer">
          <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
        </div>
      </div>
    </div>
  );
}

export default Login;
