import axios from 'axios';

// Create an axios instance with the base URL
const api = axios.create({
  baseURL: 'http://localhost:8000', // Updated to correct backend URL
  headers: {
    'Content-Type': 'application/json',
  }
});

// Request interceptor - adds auth token if available
api.interceptors.request.use(
  (config) => {
    // Get token from localStorage
    const token = localStorage.getItem('token');
    
    // Check if token exists
    if (token) {
      console.log(`API Request: Using token for ${config.url}`);
      
      // Check if the token starts with "eyJ" which indicates it's likely a JWT
      if (token.startsWith('eyJ')) {
        // The token itself is already the JWT value, no need to prefix it
        config.headers.Authorization = `Bearer ${token}`;
        console.log('Using Bearer JWT format');
      } else {
        // Use Token format for other token types
        config.headers.Authorization = `Token ${token}`;
      }
      
      // Debugging log
      console.log('Authorization header:', config.headers.Authorization.substring(0, 15) + '...');
    } else {
      console.log('API Request: No token found');
    }
    
    return config;
  },
  (error) => {
    console.error('Request interceptor error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor - handles auth errors
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    // Handle 401 Unauthorized errors
    if (error.response && error.response.status === 401) {
      console.error('401 Unauthorized error from API:', {
        url: error.config.url,
        method: error.config.method,
        headers: error.config.headers.Authorization ? 
          error.config.headers.Authorization.substring(0, 15) + '...' : 'none'
      });
    }
    
    return Promise.reject(error);
  }
);

// Auth functions
export const loginUser = async (credentials) => {
  try {
    console.log('API: Sending login request with:', credentials.email);
    const response = await api.post('/api/auth/login/', credentials);
    console.log('API: Login response status:', response.status);
    
    // Store the token properly based on the response format
    if (response.data) {
      console.log('Login response data structure:', 
        Object.keys(response.data).join(', '));
      
      let token = null;
      
      // Check different possible token formats in the response
      if (response.data.token) {
        token = response.data.token;
        console.log('Found token in response.data.token');
      } else if (response.data.access) {
        // Some APIs return { access: "token" } for JWT
        token = response.data.access;
        console.log('Found token in response.data.access');
      } else if (response.data.key) {
        // Django Rest Auth often returns { key: "token" }
        token = response.data.key;
        console.log('Found token in response.data.key');
      } else if (typeof response.data === 'string') {
        // Some APIs just return the token as a string
        token = response.data;
        console.log('Found token as string response');
      } else {
        // Try to find any property that might be a token
        for (const key in response.data) {
          const value = response.data[key];
          if (typeof value === 'string' && 
              (value.length > 20 || value.startsWith('eyJ'))) {
            token = value;
            console.log(`Found possible token in response.data.${key}`);
            break;
          }
        }
      }
      
      if (token) {
        // Log token format and beginning to help debug
        console.log('Token format check:');
        console.log('- Length:', token.length);
        console.log('- Starts with:', token.substring(0, 10) + '...');
        console.log('- Appears to be JWT:', token.startsWith('eyJ'));
        
        // Store the token
        localStorage.setItem('token', token);
        
        // Also store user ID if available for profile access
        if (response.data.user_id) {
          sessionStorage.setItem('userId', response.data.user_id);
        } else if (response.data.id) {
          sessionStorage.setItem('userId', response.data.id);
        } else if (response.data.user && response.data.user.id) {
          sessionStorage.setItem('userId', response.data.user.id);
        }
        
        // Store email for fallback identification
        sessionStorage.setItem('lastLoginEmail', credentials.email);
        
        // Check if name is included in response and store it
        if (response.data.name) {
          sessionStorage.setItem('userName', response.data.name);
        } else if (response.data.username) {
          sessionStorage.setItem('userName', response.data.username);
        } else if (response.data.user && response.data.user.name) {
          sessionStorage.setItem('userName', response.data.user.name);
        } else if (response.data.user && response.data.user.username) {
          sessionStorage.setItem('userName', response.data.user.username);
        }
        
        return response.data;
      } else {
        console.error('No token found in login response:', response.data);
        throw new Error('Login failed: No token received');
      }
    } else {
      throw new Error('Login failed: No data received');
    }
  } catch (error) {
    console.error('API: Login error:', error.message);
    if (error.response) {
      console.error('API: Server response:', error.response.data);
    }
    throw new Error(error.response?.data?.message || 'Login failed. Please try again.');
  }
};

export const signupUser = async (userData) => {
  try {
    const response = await api.post('/api/auth/signup/', userData);
    return response.data;
  } catch (error) {
    console.error('API: Signup error:', error.message);
    throw new Error(error.response?.data?.message || 'Signup failed. Please try again.');
  }
};

// User profile
export const getUserProfile = async () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('Authentication required');
    }

    const response = await api.get('/apiV1/profile/', {
      headers: {
        Authorization: `Token ${token}`,
      },
    });

    console.log('Profile response:', response.status, response.data);
    return response.data;
  } catch (error) {
    console.error('Error fetching user profile:', error.response ? error.response.status : error.message);
    
    // Create a fallback user object if profile fetch fails
    const email = sessionStorage.getItem('lastLoginEmail');
    if (email) {
      return {
        email: email,
        name: 'User', // Fallback name
        profile_picture: null,
      };
    }
    throw error;
  }
};

// Directories
export const getDirectories = async () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('Authentication required');
    }

    // Try with trailing slash first (Django often requires this)
    const response = await api.get('/apiV1/directories/', {
      headers: {
        Authorization: `Token ${token}`, // Using Token format for Django REST Framework
      },
    });

    console.log('Directories response:', response.status, response.data);
    return response.data;
  } catch (error) {
    // If we get a 404 with trailing slash, try without
    if (error.response && error.response.status === 404) {
      try {
        const response = await api.get('/apiV1/directories', {
          headers: {
            Authorization: `Token ${token}`,
          },
        });
        console.log('Directories response (without trailing slash):', response.status, response.data);
        return response.data;
      } catch (retryError) {
        console.error('Error fetching directories (retry):', retryError.response ? retryError.response.status : retryError.message);
        throw retryError;
      }
    }

    console.error('Error fetching directories:', error.response ? error.response.status : error.message);
    
    // Return some mock directory data if the API fails
    return [
      { id: 1, name: 'Documents', path: '/documents', color: '#4285F4' },
      { id: 2, name: 'Images', path: '/images', color: '#34A853' },
      { id: 3, name: 'Projects', path: '/projects', color: '#FBBC05' },
      { id: 4, name: 'Reports', path: '/reports', color: '#EA4335' },
    ];
  }
};

// Chat API Functions
export const getChatHistory = async () => {
  try {
    // Since there's no specific chat history endpoint, we'll return an empty array
    // The Chat component will handle creating the initial welcome message
    return [];
  } catch (error) {
