import React, { useContext, useEffect } from 'react';
import { Outlet, useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { AuthContext } from '../context/AuthContext';
import '../styles/Base.css';

function Base() {
  const { isAuthenticated } = useContext(AuthContext);
  const location = useLocation();
  const navigate = useNavigate();
  
  // Enhanced check for login status and redirection
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    const justLoggedIn = sessionStorage.getItem('justLoggedIn');
    const loginTimestamp = sessionStorage.getItem('loginTimestamp');
    const isAuthenticated = !!token || !!storedUser;
    const isHomePage = location.pathname === '/';
    const now = Date.now();
    
    console.log('Base: Authentication check');
    console.log('Base: Path =', location.pathname);
    console.log('Base: Token exists =', !!token);
    console.log('Base: User exists =', !!storedUser);
    console.log('Base: Just logged in flag =', !!justLoggedIn);
    
    // Check if user just logged in within the last 10 seconds
    if (justLoggedIn && loginTimestamp) {
      const loginTime = parseInt(loginTimestamp);
      const timeElapsed = now - loginTime;
      
      console.log('Base: Login timestamp =', loginTime);
      console.log('Base: Time elapsed since login =', timeElapsed, 'ms');
      
      // If recent login and user is on home page, redirect to directories
      if (timeElapsed < 10000 && isHomePage && isAuthenticated) {
        console.log('Base: Recently logged in user detected, redirecting to /directories');
        sessionStorage.removeItem('justLoggedIn');
        sessionStorage.removeItem('loginTimestamp');
        
        // Try direct navigation first
        try {
          navigate('/directories');
        } catch (e) {
          console.error('Navigation error:', e);
          // Fallback to direct page redirect
          window.location.href = '/directories';
        }
      }
    }
  }, [location.pathname, navigate]);

  return (
    <div className="base-container">
      <Navbar />
      <main className="content-container">
        <Outlet />
      </main>
      <Footer />
    </div>
  );
}

export default Base;
