import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/DirectoryDetail.css';
import logo from '../images/OatmealaLogoi (1).png';

function DirectoryDetail() {
  const { directoryType } = useParams();
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [businesses, setBusinesses] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedState, setSelectedState] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Format the directory type for display (convert path slugs to title)
  const formatTitle = (slug) => {
    if (!slug) return '';
    return slug.split('-')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const title = formatTitle(directoryType);

  // Get placeholder image based on directory type
  const getHeaderImage = () => {
    // Instead of importing all images, use a generic approach
    try {
      // First, try to use the directory type as part of the image name
      // This allows for a more dynamic approach without needing to import all images
      // Attempt to get the image from the public folder
      return `/images/${directoryType.replace(/-/g, '_')}.png`;
    } catch (error) {
      console.error("Error loading image:", error);
      // Default to a placeholder if the specific image can't be found
      return '/images/placeholder.png';
    }
  };

  // Fetch countries when component mounts
  useEffect(() => {
    const loadCountries = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(`/api/countries`);
        if (!response.ok) {
          throw new Error('Failed to fetch countries');
        }
        const data = await response.json();
        setCountries(data);
      } catch (error) {
        console.error('Error loading countries:', error);
        setError('Failed to load countries. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    loadCountries();
  }, []);

  // Fetch states when a country is selected
  useEffect(() => {
    if (!selectedCountry) {
      setStates([]);
      return;
    }

    const loadStates = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(`/api/states/${encodeURIComponent(selectedCountry)}`);
        if (!response.ok) {
          throw new Error('Failed to fetch states');
        }
        const data = await response.json();
        setStates(data);
      } catch (error) {
        console.error('Error loading states:', error);
        setError('Failed to load states. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    loadStates();
  }, [selectedCountry]);

  // Fetch businesses when both country and state are selected
  useEffect(() => {
    if (!selectedCountry || !selectedState) {
      setBusinesses([]);
      return;
    }

    const loadBusinesses = async () => {
      try {
        setIsLoading(true);
        const encodedTitle = encodeURIComponent(title);
        const encodedCountry = encodeURIComponent(selectedCountry);
        const encodedState = encodeURIComponent(selectedState);
        
        const apiUrl = `/api/businesses-by-encoded-title?title=${encodedTitle}&country=${encodedCountry}&state=${encodedState}`;
        
        const response = await fetch(apiUrl);
        if (!response.ok) {
          throw new Error('Failed to fetch businesses');
        }
        
        const data = await response.json();
        setBusinesses(data);
      } catch (error) {
        console.error('Error loading businesses:', error);
        setError('Failed to load businesses. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    loadBusinesses();
  }, [selectedCountry, selectedState, title]);

  // Clear all selections
  const clearAll = () => {
    setSelectedCountry('');
    setSelectedState('');
    setBusinesses([]);
  };

  // Debug location
  const debugLocation = async () => {
    if (!selectedCountry || !selectedState) {
      alert('Please select both country and state first');
      return;
    }

    try {
      setIsLoading(true);
      const response = await fetch(`/api/location-info/${encodeURIComponent(selectedCountry)}/${encodeURIComponent(selectedState)}`);
      if (!response.ok) {
        throw new Error('Failed to fetch location info');
      }
      
      const data = await response.json();
      
      let message = `Location: ${selectedCountry}, ${selectedState}\n`;
      message += `Total businesses: ${data.total_businesses}\n\n`;
      message += 'Business types available in this location:\n';
      
      for (const [type, count] of Object.entries(data.business_types)) {
        message += `- ${type}: ${count} businesses\n`;
      }
      
      alert(message);
    } catch (error) {
      console.error('Error checking location:', error);
      alert('Failed to check location data');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <nav className="sidebar">
        <img src={logo} alt="Oatmeal AI Logo" className="sidebar-logo" />
        <h2>Oatmeal AI</h2>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/chat">Chat</Link></li>
        </ul>
      </nav>
      
      <main>
        <header>
          <img 
            src={getHeaderImage()} 
            alt={`${title}`} 
            className="header-image" 
          />
          <h1>{title}</h1>
        </header>
        
        <div className="filter-section">
          <div className="filter-group">
            <label>Country</label>
            <select 
              value={selectedCountry}
              onChange={(e) => setSelectedCountry(e.target.value)}
            >
              <option value="">Select Country</option>
              {countries.map((country, index) => (
                <option key={index} value={country}>{country}</option>
              ))}
            </select>
          </div>
          
          <div className="filter-group">
            <label>State</label>
            <select 
              value={selectedState}
              onChange={(e) => setSelectedState(e.target.value)}
              disabled={!selectedCountry}
            >
              <option value="">Select State</option>
              {states.map((state, index) => (
                <option key={index} value={state}>{state}</option>
              ))}
            </select>
          </div>
          
          <button className="clear-btn" onClick={clearAll}>Clear all</button>
          <button 
            className="debug-btn" 
            onClick={debugLocation}
          >
            Debug Location
          </button>
        </div>
        
        <section className="candidates">
          <h2>Result: <span id="resultCount">{businesses.length}</span> Businesses</h2>
          <div id="business-list">
            {isLoading ? (
              <div className="loading">Loading...</div>
            ) : error ? (
              <div className="error-message">{error}</div>
            ) : businesses.length === 0 && (selectedCountry && selectedState) ? (
              <div className="error-message">No businesses found for this location. Please try a different selection.</div>
            ) : (
              businesses.map((business, index) => (
                <div className="candidate" key={index}>
                  <div className="candidate-info">
                    <h3>{business}</h3>
                    <span className="open-btn">Open</span>
                  </div>
                  <button className="detail-btn">Detail</button>
                </div>
              ))
            )}
          </div>
        </section>
      </main>
    </div>
  );
}

export default DirectoryDetail;
