export const sendChatMessage = async (message) => {
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('Authentication required');
    }

    // Special handling for test connection messages
    if (message === 'test_connection') {
      console.log('Testing connection to Azure RAG pipeline...');
      // Try different endpoints to determine if we can connect to the backend
      try {
        // First try the profile endpoint as it's more likely to exist
        const headers = {};
        
        // Use proper auth format based on token format
        if (token.startsWith('eyJ')) {
          headers.Authorization = `Bearer ${token}`;
        } else {
          headers.Authorization = `Token ${token}`;
        }
        
        await api.get('/apiV1/profile/', { headers });
        console.log('Connected to backend via profile endpoint');
        return { success: true };
      } catch (profileError) {
        console.log('Profile endpoint test failed:', profileError.response?.status);
        console.log('Response data:', profileError.response?.data);
        
        // If token is invalid, log out the user and throw error
        if (profileError.response?.status === 401 && 
            profileError.response?.data?.detail === 'Invalid token.') {
          console.error('Invalid token detected. User needs to log in again.');
          // Clear the invalid token
          localStorage.removeItem('token');
          throw new Error('Session expired. Please log in again.');
        }
        
        // Fall back to directories endpoint
        try {
          const headers = {};
        
          // Use proper auth format based on token format
          if (token.startsWith('eyJ')) {
            headers.Authorization = `Bearer ${token}`;
          } else {
            headers.Authorization = `Token ${token}`;
          }
          
          await api.get('/apiV1/directories/', { headers });
          console.log('Connected to backend via directories endpoint');
          return { success: true };
        } catch (dirError) {
          console.log('Directories endpoint test failed:', dirError.response?.status);
          
          // Last attempt with a simple chat message
          try {
            // Try a simple message to test if chat works
            const headers = {};
        
            // Use proper auth format based on token format
            if (token.startsWith('eyJ')) {
              headers.Authorization = `Bearer ${token}`;
            } else {
              headers.Authorization = `Token ${token}`;
            }
            
            const testResponse = await api.post('/apiV1/chat/', 
              { message: "test" },
              { headers }
            );
            console.log('Chat endpoint test successful');
            return { success: true };
          } catch (chatError) {
            console.error('All connection tests failed:', 
              chatError.response?.status || chatError.message);
            throw chatError;
          }
        }
      }
    }

    // Send message to the Azure-powered chat API endpoint
    console.log('Sending chat message to Azure:', message);
    
    const headers = {};
    
    // Use proper auth format based on token format
    if (token.startsWith('eyJ')) {
      headers.Authorization = `Bearer ${token}`;
    } else {
      headers.Authorization = `Token ${token}`;
    }
    
    const response = await api.post('/apiV1/chat/', {
      message: message
    }, { headers });

    console.log('Chat API response:', response.status, response.data);
    
    // Format the response for the UI
    const aiResponse = {
      id: Date.now(),
      content: response.data.response || response.data.message || "I received your message but couldn't generate a response.",
      sender: 'Azure AI',
      timestamp: new Date().toISOString(),
      isCurrentUser: false,
      sources: response.data.sources || []
    };
    
    return { 
      success: true,
      aiResponse: aiResponse
    };
    
  } catch (error) {
    console.error('Error sending chat message:', error.response?.status, error.message);
    console.error('Error details:', error.response?.data || 'No response data');
    
    // If unauthorized, throw error to redirect to login
    if (error.response && error.response.status === 401) {
      // If token is explicitly invalid, clear it
      if (error.response.data?.detail === 'Invalid token.') {
        localStorage.removeItem('token');
      }
      throw new Error('Authentication required');
    }
    
    // For test connection, just rethrow
    if (message === 'test_connection') {
      throw error;
    }
    
    // Create a fallback response
    const errorResponse = {
      id: Date.now(),
      content: "I'm having trouble connecting to the Azure OpenAI service. Please check your internet connection and try again later.",
      sender: 'Azure AI',
      timestamp: new Date().toISOString(),
      isCurrentUser: false
    };
    
    return {
      success: false,
      aiResponse: errorResponse
    };
  }
};

// Function to diagnose API endpoints
export const checkApiEndpoints = async () => {
  const token = localStorage.getItem('token');
  if (!token) {
    console.error('No authentication token available');
    return { authenticated: false, endpoints: [] };
  }
  
  // Prepare headers
  const headers = {
    Authorization: `Token ${token}`,
  };
  
  // List of endpoints to test
  const endpoints = [
    // Common API endpoints
    { method: 'GET', url: '/api/auth/profile/' },
    { method: 'GET', url: '/api/directories/' },
    { method: 'GET', url: '/api/chat/' },
    { method: 'POST', url: '/api/chat/', data: { message: 'test' } },
    
    // V1 API endpoints 
    { method: 'GET', url: '/apiV1/profile/' },
    { method: 'GET', url: '/apiV1/directories/' },
    { method: 'GET', url: '/apiV1/ping/' },
    { method: 'POST', url: '/apiV1/chat/', data: { message: 'test' } },
    
    // Azure-specific endpoints (if any)
    { method: 'GET', url: '/apiV1/azure/status/' },
  ];
  
  const results = [];
  
  // Test each endpoint
  for (const endpoint of endpoints) {
    try {
      console.log(`Testing ${endpoint.method} ${endpoint.url}...`);
      
      const config = {
        method: endpoint.method,
        url: endpoint.url,
        headers: headers,
        data: endpoint.data || null
      };
      
      const response = await axios(config);
      
      results.push({
        endpoint: endpoint.url,
        method: endpoint.method,
        status: response.status,
        available: true,
        data: typeof response.data === 'object' ? 'Object received' : 'Data received'
      });
      
      console.log(`✅ ${endpoint.method} ${endpoint.url} - Status: ${response.status}`);
    } catch (error) {
      results.push({
        endpoint: endpoint.url,
        method: endpoint.method,
        status: error.response?.status || 'Network Error',
        available: false,
        error: error.response?.data || error.message
      });
      
      console.log(`❌ ${endpoint.method} ${endpoint.url} - Error: ${error.response?.status || error.message}`);
    }
  }
  
  // Log the summary
  const availableEndpoints = results.filter(e => e.available);
  console.log(`API Endpoint Check Summary: ${availableEndpoints.length}/${endpoints.length} endpoints available`);
  console.table(results);
  
  return {
    authenticated: true,
    endpoints: results,
    availableCount: availableEndpoints.length,
    totalCount: endpoints.length
  };
};

