import React, { useState, useContext, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { loginUser, getUserProfile } from '../services/api';
import axios from 'axios';
import '../styles/Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [backendStatus, setBackendStatus] = useState(null);
  const [testingBackend, setTestingBackend] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [passwordVisible, setPasswordVisible] = useState(false);
  const [availableEndpoints, setAvailableEndpoints] = useState([]);
  const [testingEndpoints, setTestingEndpoints] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [endpointResults, setEndpointResults] = useState(null);
  const passwordTooltipRef = useRef(null);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  // Test backend connectivity
  const testBackendConnection = async () => {
    setTestingBackend(true);
    setBackendStatus(null);
    
    const testEndpoints = [
      'http://127.0.0.1:8000/'
    ];
    
    let connected = false;
    
    for (const endpoint of testEndpoints) {
      try {
        const response = await axios.get(endpoint, { timeout: 5000 });
        if (response.status >= 200 && response.status < 500) {
          connected = true;
          setBackendStatus({
            connected: true,
            message: 'Backend server is online.',
            endpoint: endpoint
          });
          break;
        }
      } catch (error) {
        if (error.response && error.response.status === 404) {
          connected = true;
          setBackendStatus({
            connected: true,
            message: 'Backend server is online (404 response).',
            endpoint: endpoint
          });
          break;
        }
      }
    }
    
    if (!connected) {
      setBackendStatus({
        connected: false,
        message: 'Cannot connect to backend server. Please ensure the server is running.',
        error: 'All connection attempts failed'
      });
    }
    
    setTestingBackend(false);
  };
  
  // Test which login endpoints are available
  const testLoginEndpoints = async () => {
    setTestingEndpoints(true);
    setAvailableEndpoints([]);
    
    const endpoints = [
      'http://127.0.0.1:8000/api/auth/login/',
      'http://127.0.0.1:8000/apiV1/api/auth/login/',
      'http://127.0.0.1:8000/users/api/login/',
      'http://127.0.0.1:8000/apiV1/users/api/login/',
      'http://127.0.0.1:8000/login/',
      'http://127.0.0.1:8000/chatbot/login/'
    ];
    
    const available = [];
    
    for (const endpoint of endpoints) {
      try {
        // Just check if the endpoint exists using OPTIONS method
        const response = await axios({
          method: 'options',
          url: endpoint,
          timeout: 3000
        });
        
        // If no error, endpoint might be valid
        available.push({
          url: endpoint,
          status: response.status,
          statusText: response.statusText
        });
      } catch (error) {
        // Even a 405 Method Not Allowed is good, it means the endpoint exists
        if (error.response && error.response.status !== 404) {
          available.push({
            url: endpoint,
            status: error.response.status,
            statusText: error.response.statusText
          });
        }
      }
    }
    
    setAvailableEndpoints(available);
    setTestingEndpoints(false);
  };

  // New function to test backend API endpoints
  const testApiEndpoints = async () => {
    setTestingEndpoints(true);
    setEndpointResults(null);
    
    try {
      // First check if the server is running at all
      const serverCheck = {
        'Django Server Status': null
      };
      
      try {
        // Try to hit the health check endpoint
        const healthResponse = await axios({
          method: 'GET',
          url: 'http://127.0.0.1:8000/users/health/',
          timeout: 3000 // 3 second timeout
        });
        
        serverCheck['Django Server Status'] = {
          status: 'Running',
          statusCode: healthResponse.status,
          message: healthResponse.data?.status || 'OK'
        };
      } catch (error) {
        if (error.response) {
          // The server responded with an error, but it's running
          serverCheck['Django Server Status'] = {
            status: 'Running but returned error',
            statusCode: error.response.status,
            error: 'Server responded with an error'
          };
        } else if (error.request) {
          // The request was made but no response was received
          try {
            // Try localhost instead of 127.0.0.1
            const localhostResponse = await axios({
              method: 'GET',
              url: 'http://localhost:8000/users/health/',
              timeout: 3000
            });
            
            serverCheck['Django Server Status'] = {
              status: 'Running on localhost',
              statusCode: localhostResponse.status,
              message: 'Server is accessible via localhost, not 127.0.0.1'
            };
          } catch (localhostError) {
            serverCheck['Django Server Status'] = {
              status: 'Not running or not accessible',
              error: 'Could not connect to Django server on 127.0.0.1 or localhost'
            };
          }
        } else {
          serverCheck['Django Server Status'] = {
            status: 'Error checking server',
            error: error.message
          };
        }
      }
      
      // Define all backend endpoints to test
      const endpoints = [
        'http://127.0.0.1:8000/users/api/login/',
        'http://localhost:8000/users/api/login/',
        'http://127.0.0.1:8000/api/login/',
        'http://localhost:8000/api/login/',
        'http://127.0.0.1:8000/users/api/register/',
        'http://localhost:8000/users/api/register/'
      ];
      
      const results = {...serverCheck};
      
      // Test each endpoint
      for (const endpoint of endpoints) {
        try {
          // Use OPTIONS request to check if endpoint exists
          const response = await axios({
            method: 'OPTIONS',
            url: endpoint,
            timeout: 3000 // 3 second timeout
          });
          
          results[endpoint] = {
            status: 'Available',
            statusCode: response.status,
            methods: response.headers?.['allow'] || 'Unknown'
          };
          
        } catch (error) {
          // Handle different error types
          if (error.response) {
            // Server responded with a non-2xx status
            if (error.response.status === 404) {
              results[endpoint] = {
                status: 'Not Found (404)',
                statusCode: error.response.status,
                error: 'Endpoint does not exist at this URL'
              };
            } else if (error.response.status === 403 || error.response.status === 401) {
              results[endpoint] = {
                status: 'Available (requires authentication)',
                statusCode: error.response.status,
                message: 'Endpoint exists but requires authentication'
              };
            } else {
              results[endpoint] = {
                status: 'Available but returned error',
                statusCode: error.response.status,
                error: error.response.data?.detail || 'Server error'
              };
            }
          } else if (error.request) {
            // Request was made but no response received
            results[endpoint] = {
              status: 'No response',
              error: 'Request timeout or server not responding'
            };
          } else {
            // Error in setting up the request
            results[endpoint] = {
              status: 'Error',
              error: error.message
            };
          }
        }
      }
      
      // Add tips based on the results
      results['TROUBLESHOOTING'] = {
        status: 'Tips',
        tips: [
          'Make sure your Django server is running with `python manage.py runserver`',
          'Check that your server is running on port 8000',
          'Verify URL configurations in users/urls.py and backend/urls.py',
          'Try using localhost instead of 127.0.0.1 if one works but not the other',
          'Check if CORS is properly configured in your Django settings'
        ]
      };
      
      setEndpointResults(results);
      console.log('API Endpoint Test Results:', results);
      
      // Check if we found any working endpoints
      const workingEndpoints = Object.entries(results)
        .filter(([key, result]) => 
          key !== 'TROUBLESHOOTING' && 
          key !== 'Django Server Status' && 
          (result.status === 'Available' || result.status === 'Available (requires authentication)')
        )
        .map(([endpoint]) => endpoint);
      
      setAvailableEndpoints(workingEndpoints);
      
    } catch (error) {
      console.error('Error testing endpoints:', error);
      setEndpointResults({ 
        error: error.message,
        TROUBLESHOOTING: {
          status: 'Error',
          tips: [
            'Make sure your Django server is running',
            'Check your network connection',
            'Verify that CORS is enabled on your server'
          ]
        }
      });
    } finally {
      setTestingEndpoints(false);
    }
  };

  useEffect(() => {
    testBackendConnection();
  }, []);

  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };

  const handlePasswordFocus = () => {
    if (passwordTooltipRef.current) {
      passwordTooltipRef.current.style.display = 'block';
    }
  };

  const handlePasswordBlur = () => {
    if (passwordTooltipRef.current) {
      passwordTooltipRef.current.style.display = 'none';
    }
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (passwordTooltipRef.current) {
      if (e.target.value.length >= 8) {
        passwordTooltipRef.current.style.display = 'none';
      } else {
        passwordTooltipRef.current.style.display = 'block';
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    // Basic validation
    if (!email) {
      setError('Email is required');
      setLoading(false);
      return;
    }
    
    if (!password) {
      setError('Password is required');
      setLoading(false);
      return;
    }
    
    try {
      console.log('Submitting login form with:', { email, password: '*******' });
      const response = await loginUser({ email, password });
      
      console.log('Login response:', response);
      
      if (response.success) {
        // Store user state via context
        await login(response);
        
        // If remember me is checked, save email to localStorage
        if (rememberMe) {
          localStorage.setItem('rememberedEmail', email);
        } else {
          localStorage.removeItem('rememberedEmail');
        }
        
        console.log('Redirecting to directories page...');
        // Redirect to directories page after successful login
        navigate('/directories');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError(err.message || 'Failed to log in. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  // Check for saved email on component mount
  useEffect(() => {
    const savedEmail = localStorage.getItem('savedEmail');
    if (savedEmail) {
      setEmail(savedEmail);
      setRememberMe(true);
    }
    
    // Check if already logged in
    const token = localStorage.getItem('token');
    if (token) {
      navigate('/directories');
    }
  }, [navigate]);

  // Image animation effect from original code
  useEffect(() => {
    const handleMouseMove = (event) => {
      const image = document.querySelector('.right-corner-image');
      if (!image) return;
      
      const rect = image.getBoundingClientRect();
      
      // Check if mouse is over the image
      const isMouseOverImage = (
        event.clientX >= rect.left &&
        event.clientX <= rect.right &&
        event.clientY >= rect.top &&
        event.clientY <= rect.bottom
      );
      
      // Only update the position if the mouse is not over the image
      if (!isMouseOverImage) {
        image.style.transform = `translate(${event.clientX / 100 - 20}px, ${event.clientY / 100 - 20}px)`;
      }
    };
    
    document.addEventListener('mousemove', handleMouseMove);
    
    // Clean up event listener when component unmounts
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="login-page">
      <nav className="navbar">
        <img src="/logo.png" alt="Logo" className="logo" />
        <div className="nav-links">
          <a href="/">Home</a>
          <a href="/about">About Us</a>
          <a href="/team">Our Team</a>
          <a href="/contact">Contact Us</a>
        </div>
      </nav>
      
      <div className="container">
        <div className="form-container">
          <h2>Sign in</h2>
          
          {error && <div className="error-message">{error}</div>}
          
          {backendStatus && !backendStatus.connected && (
            <div className="error-message">
              {backendStatus.message}
              <button 
                onClick={testBackendConnection}
                disabled={testingBackend}
                className="retry-button"
              >
                {testingBackend ? 'Testing...' : 'Retry Connection'}
              </button>
            </div>
          )}
          
          {backendStatus && backendStatus.connected && (
            <div className="api-test-section">
              <button 
                onClick={testLoginEndpoints}
                disabled={testingEndpoints}
                className="test-api-button"
              >
                {testingEndpoints ? 'Testing API Endpoints...' : 'Test Login API Endpoints'}
              </button>
              
              {availableEndpoints.length > 0 && (
                <div className="endpoint-results">
                  <h3>Available Login Endpoints:</h3>
                  <ul>
                    {availableEndpoints.map((endpoint, index) => (
                      <li key={index}>
                        {endpoint.url} - {endpoint.status} {endpoint.statusText}
                      </li>
                    ))}
                  </ul>
                  <p className="endpoint-tip">
                    Try using one of these endpoints in your login request.
                  </p>
                </div>
              )}
            </div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="textbox">
              <input 
                type="email" 
                id="email" 
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="example@gmail.com"
                required 
              />
              <label className="label" htmlFor="email">Email</label>
            </div>
            
            <div className="textbox">
              <input 
                type={passwordVisible ? "text" : "password"}
                id="password" 
                value={password}
                onChange={handlePasswordChange}
                onFocus={handlePasswordFocus}
                onBlur={handlePasswordBlur}
                required 
              />
              <label className="label" htmlFor="password">Password</label>
              <span className="eye-icon" onClick={togglePasswordVisibility}>
                {passwordVisible ? 'üôâ' : 'üôà'}
              </span>
              <span className="tooltip" ref={passwordTooltipRef}>Enter at least 8 characters</span>
            </div>
            
            <div className="checkbox-container">
              <input 
                type="checkbox" 
                id="rememberMe"
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)} 
              />
              <label htmlFor="rememberMe">Remember me</label>
              <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
            </div>
            
            <div className="form-group">
              <button 
                type="submit" 
                className="login-button" 
                disabled={loading || (backendStatus && !backendStatus.connected)}
              >
                {loading ? 'Signing in...' : 'Sign in'}
              </button>
            </div>
          </form>
          
          <div className="signup-container">
            <Link to="/signup" className="signup-button">No account? Join Us ü§ù</Link>
          </div>

          {/* API Testing Section */}
          <div className="api-test-section">
            <button 
              type="button" 
              className="test-api-button" 
              onClick={testApiEndpoints}
              disabled={testingEndpoints}
            >
              {testingEndpoints ? "Testing API Endpoints..." : "Test Backend Connectivity"}
            </button>
            
            {endpointResults && (
              <div className="endpoint-results">
                <h3>Available Login Endpoints:</h3>
                <ul>
                  {Object.entries(endpointResults).map(([endpoint, result]) => (
                    <li key={endpoint}>
                      <strong>{endpoint}</strong> - {result.status} 
                      {result.statusCode && ` (${result.statusCode})`}
                      {result.methods && ` - Allowed methods: ${result.methods}`}
                      {result.error && ` - ${result.error}`}
                    </li>
                  ))}
                </ul>
                <button 
                  type="button" 
                  className="retry-button"
                  onClick={testApiEndpoints}
                >
                  Retry Test
                </button>
              </div>
            )}
          </div>
        </div>
        
        <img src="/hens.jpg" alt="Right Corner Image" className="right-corner-image" />
      </div>
    </div>
  );
}

export default Login;
