import React, { useState, useEffect, useRef } from 'react';
import { Navigate } from 'react-router-dom';
import { getChatHistory, sendChatMessage, getUserProfile, checkApiEndpoints } from '../services/api';
import '../styles/Chat.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('checking'); // 'checking', 'online', 'offline'
  const [retryCount, setRetryCount] = useState(0);
  const [userProfile, setUserProfile] = useState(null);
  const [diagnosticMode, setDiagnosticMode] = useState(false);
  const [diagnosticResults, setDiagnosticResults] = useState(null);
  const messagesEndRef = useRef(null);
  
  // Check authentication first
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      // Try to get user profile
      getUserProfile()
        .then(profile => {
          console.log('Loaded user profile:', profile);
          setUserProfile(profile);
          sessionStorage.setItem('userName', profile.name || profile.email || 'User');
        })
        .catch(err => {
          console.error('Failed to load profile:', err);
        });
    }
    setLoading(false);
  }, []);
  
  // Function to test backend connectivity
  const testBackendConnection = async () => {
    try {
      // Using a simple test message to check if backend responds
      if (isAuthenticated) {
        console.log('Attempting to connect to Azure chatbot...');
        
        // Try up to 3 times with a short delay
        for (let attempt = 1; attempt <= 3; attempt++) {
          try {
            console.log(`Connection attempt ${attempt}/3...`);
            const testResponse = await sendChatMessage('test_connection');
            
            // If we get here, we successfully connected
            console.log('Azure connection test successful!');
            setConnectionStatus('online');
            return true;
          } catch (attemptError) {
            console.error(`Attempt ${attempt} failed:`, attemptError.message);
            
            if (attempt < 3) {
              // Wait a bit before retrying
              await new Promise(resolve => setTimeout(resolve, 1000));
            } else {
              // Last attempt failed, throw error to be caught in outer catch
              throw attemptError;
            }
          }
        }
      }
      return false;
    } catch (error) {
      console.error('Azure connection failed after all attempts:', error);
      setConnectionStatus('offline');
      
      // Check browser console for detailed information
      console.log('Please check browser console for detailed error information');
      console.log('If you are a developer, check the network tab for failed requests');
      
      return false;
    }
  };

  // Function to run API diagnostics
  const runDiagnostics = async () => {
    setDiagnosticMode(true);
    try {
      const results = await checkApiEndpoints();
      setDiagnosticResults(results);
      
      // Add a diagnostic message to the chat
      const diagMessage = {
        id: Date.now(),
        content: `API Diagnostics: ${results.availableCount}/${results.totalCount} endpoints available. Check browser console for details.`,
        sender: 'System',
        timestamp: new Date().toISOString(),
        isCurrentUser: false,
        isDiagnostic: true
      };
      
      setMessages(prev => [...prev, diagMessage]);
      
      // If we found working endpoints, try connecting again
      if (results.availableCount > 0) {
        const workingChatEndpoint = results.endpoints.find(
          e => e.available && e.method === 'POST' && e.url.includes('/chat/')
        );
        
        if (workingChatEndpoint) {
          await testBackendConnection();
        }
      }
      
    } catch (error) {
      console.error('Diagnostics failed:', error);
      
      // Add error message
      const errorMessage = {
        id: Date.now(),
        content: "Diagnostics failed. Please check your network connection and browser console for details.",
        sender: 'System',
        timestamp: new Date().toISOString(),
        isCurrentUser: false,
        isDiagnostic: true
      };
      
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setDiagnosticMode(false);
    }
  };

  // Fetch messages when component mounts
  useEffect(() => {
    if (!isAuthenticated) return;

    const fetchMessages = async () => {
      try {
        setLoading(true);
        const data = await getChatHistory();
        if (Array.isArray(data) && data.length > 0) {
          setMessages(data);
          // If we got history, we're online
          setConnectionStatus('online');
        } else if (messages.length === 0) {
          // Add a welcome message if we don't have any messages
          setMessages([{
            id: Date.now(),
            content: "Hello! I'm the AI Assistant powered by Azure OpenAI. How can I help you today?",
            sender: 'Azure AI',
            timestamp: new Date().toISOString(),
            isCurrentUser: false
          }]);
          
          // Try to connect to backend to set proper status
          await testBackendConnection();
        }
      } catch (error) {
        console.error("Failed to fetch messages:", error);
        
        // If we've tried less than 3 times, attempt to test connectivity again
        if (retryCount < 3) {
          setRetryCount(prevCount => prevCount + 1);
          await testBackendConnection();
        } else {
          setConnectionStatus('offline');
        }
        
        // If no messages are present, add a welcome message
        if (messages.length === 0) {
          setMessages([{
            id: Date.now(),
            content: "Hello! I'm the AI Assistant powered by Azure OpenAI. I might be operating in offline mode, but I'll do my best to assist you.",
            sender: 'Azure AI',
            timestamp: new Date().toISOString(),
            isCurrentUser: false
          }]);
        }
      } finally {
        setLoading(false);
      }
    };
    
    fetchMessages();
    
    // We'll also explicitly test the connection
    const checkConnection = async () => {
      await testBackendConnection();
    };
    
    // Check connection every 30 seconds
    const intervalId = setInterval(checkConnection, 30000); 
    
    // Initial connection check
    checkConnection();
    
    return () => clearInterval(intervalId);
  }, [isAuthenticated, retryCount]);
  
  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);
  
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || isProcessing) return;
    
    try {
      setIsProcessing(true);
      
      // Add user message to UI immediately
      const userMessageContent = newMessage.trim();
      const userMessage = {
        id: Date.now(),
        content: userMessageContent,
        sender: 'User',
        timestamp: new Date().toISOString(),
        isCurrentUser: true
      };
      
      setMessages(prevMessages => [...prevMessages, userMessage]);
      setNewMessage('');
      
      // Add temporary "thinking" message
      const thinkingMessage = {
        id: Date.now() + 1,
        content: "Thinking...",
        sender: 'Azure AI',
        timestamp: new Date().toISOString(),
        isCurrentUser: false,
        isThinking: true
      };
      
      setMessages(prevMessages => [...prevMessages, thinkingMessage]);
      
      // Send message to Azure RAG pipeline
      const response = await sendChatMessage(userMessageContent);
      
      // Replace thinking message with actual response
      setMessages(prevMessages => {
        const filteredMessages = prevMessages.filter(msg => !msg.isThinking);
        return [
          ...filteredMessages, 
          response.aiResponse
        ];
      });
      
      // If we got a successful response, we're online
      setConnectionStatus('online');
      
    } catch (error) {
      console.error("Failed to process message:", error);
      setConnectionStatus('offline');
      
      // Add error message
      const errorMessage = {
        id: Date.now() + 2,
        content: "Sorry, I couldn't connect to the Azure OpenAI service. I might be operating in offline mode at the moment. Please try again later.",
        sender: 'Azure AI',
        timestamp: new Date().toISOString(),
        isCurrentUser: false
      };
      
      setMessages(prevMessages => {
        const filteredMessages = prevMessages.filter(msg => !msg.isThinking);
        return [...filteredMessages, errorMessage];
      });
      
    } finally {
      setIsProcessing(false);
    }
  };
  
  if (loading) {
    return <div className="loading-spinner">Loading chat...</div>;
  }

  // Redirect to login if not authenticated
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }
  
  // Get display name - first from profile, then session, then fallback
  const displayName = 
    (userProfile && (userProfile.name || userProfile.username)) || 
    sessionStorage.getItem('userName') ||
    sessionStorage.getItem('lastLoginEmail') || 
    'User';
    
  const firstName = displayName.split(' ')[0];
  
  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>AI Assistant (Azure OpenAI)</h2>
        {connectionStatus !== 'checking' && (
          <div className={`connection-status ${connectionStatus}`}>
            {connectionStatus === 'online' ? 'Connected to Azure' : 'Azure Offline'}
          </div>
        )}
      </div>
      
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="no-messages">
            <p>Hello {firstName}, welcome to the AI Assistant!</p>
            <p className="hint">Try asking: "What can this application do for me?" or "Tell me about this project"</p>
          </div>
        ) : (
          messages.map((msg) => (
            <div 
              key={msg.id} 
              className={`message ${msg.isCurrentUser ? 'message-mine' : 'message-other'} ${msg.isThinking ? 'thinking' : ''} ${msg.isDiagnostic ? 'diagnostic' : ''}`}
            >
              <div className="message-sender">{msg.isCurrentUser ? displayName : msg.sender}</div>
              <div className="message-content">
                {msg.content}
                {msg.sources && msg.sources.length > 0 && (
                  <div className="message-sources">
                    <p className="sources-title">Sources:</p>
                    <ul>
                      {msg.sources.map((source, index) => (
                        <li key={index}>
                          {source.title || source.url || source.path || "Reference document"}
                          {source.url && <a href={source.url} target="_blank" rel="noopener noreferrer"> (link)</a>}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
              <div className="message-time">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>
      
      <form onSubmit={handleSendMessage} className="message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Ask me anything..."
          className="message-input"
          disabled={isProcessing}
        />
        <button 
          type="submit" 
          className={`send-button ${isProcessing ? 'disabled' : ''}`}
          disabled={isProcessing}
        >
          {isProcessing ? 'Sending...' : 'Send'}
        </button>
      </form>
      
      {connectionStatus === 'offline' && (
        <div className="offline-notice">
          <p>You're currently disconnected from Azure OpenAI services. The assistant will provide general responses without accessing the full knowledge base.</p>
          <div className="offline-actions">
            <button 
              onClick={() => testBackendConnection()} 
              className="retry-button"
              disabled={isProcessing || diagnosticMode}
            >
              Retry Azure Connection
            </button>
            <button 
              onClick={runDiagnostics}
              className="diagnostic-button"
              disabled={isProcessing || diagnosticMode}
            >
              {diagnosticMode ? 'Running Diagnostics...' : 'Run API Diagnostics'}
            </button>
          </div>
          <div className="troubleshooting-tips">
            <p className="tips-header">Troubleshooting Tips:</p>
            <ul>
              <li>Ensure your Django backend server is running</li>
              <li>Check if Azure OpenAI services are properly configured in the backend</li>
              <li>Look at the browser console for detailed error messages</li>
              <li>Verify Azure API keys in your backend environment variables</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chat;
