import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/Directories.css';
import logo from '../images/OatmealaLogoi (1).png';
import { API_ENDPOINTS } from '../config';
import { testApi, testCountriesApi } from '../test-api';

// Mapping from URL directory types to database BusinessType values
const DIRECTORY_TYPE_TO_BUSINESS_TYPE = {
  'agricultural-associations': 'Agricultural Association',
  'artisan-producers': 'Artisan Food Producer',
  'business-resources': 'Business Resources',
  'crafter-organizations': 'Crafters Organization',
  'farmers-markets': 'Farmers Market',
  'farms-ranches': 'Farm / Ranch',
  'fiber-cooperatives': 'Fiber Cooperative',
  'fiber-mills': 'Fiber Mill',
  'fisheries': 'Fisheries',
  'fishermen': 'Fishermen',
  'food-cooperatives': 'Food Cooperative',
  'food-hubs': 'Food Hub',
  'grocery-stores': 'Grocery Store',
  'manufacturers': 'Manufacturer',
  'marinas': 'Marina',
  'meat-wholesalers': 'Meat Wholesaler',
  'real-estate-agents': 'Real Estate Agent',
  'restaurants': 'Restaurant',
  'retailers': 'Retailer',
  'service-providers': 'Service Provider',
  'universities': 'University',
  'veterinarians': 'Veterinarian',
  'vineyards': 'Vineyard',
  'wineries': 'Winery',
  'others': 'Other',
  'herb-tea-producers': 'Herb & Tea Producer'
};

// Import images with the correct file extensions
import agriAssociaImg from '../images/agri_associa.png';
import artisianImg from '../images/artisian.jpg';
import brImg from '../images/br.jpg';
import crafOrgImg from '../images/craf_org.jpg';
import farmersMarketImg from '../images/farmers_market.jpg';
import farmsRanchesImg from '../images/farms_ranches.jpg';
import fiberImg from '../images/fiber.jpg';
import fiberMillsImg from '../images/fiber_mills.jpg';
import fisheriesImg from '../images/fisheries.jpeg';
import fishermenImg from '../images/fishermen.jpeg';
import foodCopImg from '../images/food_cop.jpg';
import foodHubImg from '../images/food_hub.jpg';
import groceryStoreImg from '../images/grocery_store.jpeg';
import manfacImg from '../images/manfac.jpeg';
import marinasImg from '../images/marinas.jpeg';
import meatImg from '../images/meat.jpg';
import realEstateImg from '../images/real_estate.webp';
import restaurantsImg from '../images/restaurants.jpg';
import retailersImg from '../images/retailers.png';
import serviceProvidersImg from '../images/service_providers.webp';
import universitiesImg from '../images/universities.jpeg';
import vetImg from '../images/vet.webp';
import vineyardsImg from '../images/vineyards.jpeg';
import wineriesImg from '../images/wineries.png';
import othersImg from '../images/others.jpg';

function DirectoryDetail() {
  const { directoryType } = useParams();
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedState, setSelectedState] = useState('');
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [businesses, setBusinesses] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [expandedBusinessId, setExpandedBusinessId] = useState(null);
  const [groupedBusinesses, setGroupedBusinesses] = useState({});
  const [showGrouped, setShowGrouped] = useState(false);

  // Define businessType at the component level
  const businessType = DIRECTORY_TYPE_TO_BUSINESS_TYPE[directoryType] || '';

  // Format the directory type for display (convert path slugs to title)
  const formatTitle = (slug) => {
    if (!slug) return '';
    return slug.split('-')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const title = formatTitle(directoryType);

  // Map of directory type slugs to their corresponding images
  const directoryImages = {
    'agricultural-associations': agriAssociaImg,
    'artisan-producers': artisianImg,
    'business-resources': brImg,
    'crafter-organizations': crafOrgImg,
    'farmers-markets': farmersMarketImg,
    'farms-ranches': farmsRanchesImg,
    'fiber-cooperatives': fiberImg,
    'fiber-mills': fiberMillsImg,
    'fisheries': fisheriesImg,
    'fishermen': fishermenImg,
    'food-cooperatives': foodCopImg,
    'food-hubs': foodHubImg,
    'grocery-stores': groceryStoreImg,
    'manufacturers': manfacImg,
    'marinas': marinasImg,
    'meat-wholesalers': meatImg,
    'real-estate-agents': realEstateImg,
    'restaurants': restaurantsImg,
    'retailers': retailersImg,
    'service-providers': serviceProvidersImg,
    'universities': universitiesImg,
    'veterinarians': vetImg,
    'vineyards': vineyardsImg,
    'wineries': wineriesImg,
    'others': othersImg
  };

  // Get the correct image for the current directory type
  const getHeaderImage = () => {
    return directoryImages[directoryType] || othersImg; // Default to "others" if not found
  };

  // Function to fetch businesses based on the selected country and state
  const fetchBusinesses = useCallback(async () => {
    if (!selectedCountry || !selectedState) {
      setError('Please select both a country and state');
      setIsLoading(false);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      if (!businessType) {
        throw new Error(`Unknown directory type: ${directoryType}`);
      }

      console.log(`Fetching businesses for ${selectedState}, ${selectedCountry}, directory type: ${directoryType}`);

      // Initialize an empty array to store all results
      let allBusinesses = [];
      // Only filter by country and state, not by business type to get ALL businesses
      let nextPageUrl = `${API_ENDPOINTS.directoryBusinesses}?country=${encodeURIComponent(selectedCountry)}&state=${encodeURIComponent(selectedState)}`;

      // Set a reasonable limit to avoid infinite loops
      const MAX_PAGES = 10;
      let pageCount = 0;

      // Fetch all pages of results
      while (nextPageUrl && pageCount < MAX_PAGES) {
        console.log(`Fetching page ${pageCount + 1} from: ${nextPageUrl}`);

        const response = await fetch(nextPageUrl);

        // Log detailed response information for debugging
        console.log(`Page ${pageCount + 1} status:`, response.status);

        if (!response.ok) {
          const errorText = await response.text();
          console.error('API error response:', errorText);
          throw new Error(`API request failed with status ${response.status}: ${errorText}`);
        }

        const data = await response.json();
        console.log(`Page ${pageCount + 1} data:`, data);

        // Handle Django REST Framework paginated response
        if (data.results && Array.isArray(data.results)) {
          // Add this page's results to our collection
          allBusinesses = [...allBusinesses, ...data.results];

          // Check if there's a next page
          nextPageUrl = data.next;

          console.log(`Added ${data.results.length} businesses from page ${pageCount + 1}`);
          console.log(`Total businesses so far: ${allBusinesses.length}`);
          console.log(`Next page URL: ${nextPageUrl || 'None'}`);
        } else if (Array.isArray(data)) {
          // If the response is a direct array, just use it
          allBusinesses = [...allBusinesses, ...data];
          // No pagination in this case
          nextPageUrl = null;
        } else {
          console.error('Unexpected response format:', data);
          nextPageUrl = null;
        }

        pageCount++;
      }

      // After all pages are fetched
      console.log(`Fetched a total of ${allBusinesses.length} businesses from ${pageCount} pages`);

      // Process the data to extract and deduplicate businesses
      if (allBusinesses.length > 0) {
        // Log all business types for debugging
        const businessTypes = allBusinesses.map(b => 
          b.BusinessType || b.business_type || b.directory_type || b.type || 'Unknown'
        );
        const uniqueTypes = [...new Set(businessTypes)];
        console.log('All business types found:', uniqueTypes);
        console.log('Expected type:', businessType);

        // Initialize a Map to store unique businesses by name
        const uniqueBusinesses = new Map();

        // More flexible matching function for business types
        const matchesDirectoryType = (typeToCheck) => {
          if (!typeToCheck) return false;

          // Case 1: Exact match
          if (typeToCheck === businessType) return true;

          // Case 2: Case-insensitive match
          if (typeToCheck.toLowerCase() === businessType.toLowerCase()) return true;

          // Case 3: Check if it contains the expected type as a substring
          if (typeToCheck.toLowerCase().includes(businessType.toLowerCase())) return true;

          // Case 4: Check if expected type contains this type
          if (businessType.toLowerCase().includes(typeToCheck.toLowerCase())) return true;

          return false;
        };

        console.log(`Using flexible business type matching for "${businessType}"`);

        // First filter the businesses to include those that match the expected business type
        const filteredBusinesses = allBusinesses.filter(business => {
          const businessActualType = business.BusinessType || business.business_type || business.directory_type || business.type;
          const matches = matchesDirectoryType(businessActualType);

          // Log each business and its type for debugging
          console.log(`Business: ${business.BusinessName || business.business_name || 'Unknown'}, Type: ${businessActualType || 'Unknown'}, Match: ${matches}`);

          return matches;
        });

        console.log(`After filtering, found ${filteredBusinesses.length} businesses matching type "${businessType}"`);

        // If too few results with strict filtering, use all businesses
        const businessesToUse = filteredBusinesses.length < 10 ? allBusinesses : filteredBusinesses;
        console.log(`Using ${businessesToUse.length} businesses for display`);

        businessesToUse.forEach((business, index) => {
          // Handle if it's just a string (business name)
          if (typeof business === 'string') {
            if (!uniqueBusinesses.has(business)) {
              const businessObj = {
                id: index + 1,
                BusinessName: business,
                displayName: business,
                BusinessType: businessType // Always use the exact directory business type
              };
              uniqueBusinesses.set(business, businessObj);
            }
          } 
          // Handle if it's an object with properties
          else {
            // Try to get the business name from various possible field names
            const businessName = business.BusinessName || business.business_name || business.Business_Name || business.name || 'Unknown Business';

            if (!uniqueBusinesses.has(businessName)) {
              // Create a business object with all original fields plus our standardized fields
              const businessObj = {
                ...business, // Keep all original fields
                id: business.id || business.ID || index + 1,
                BusinessName: businessName,
                displayName: businessName,
                BusinessType: businessType // Force the correct business type
              };
              uniqueBusinesses.set(businessName, businessObj);
            }
          }
        });

        // Convert Map values to array and sort by business name
        const processedData = Array.from(uniqueBusinesses.values())
          .sort((a, b) => a.BusinessName.localeCompare(b.BusinessName));

        setBusinesses(processedData);
        console.log(`Found ${processedData.length} ${businessType} businesses after deduplication`);
      } else {
        console.log('No businesses found or unexpected data format');
        setBusinesses([]);
      }
    } catch (err) {
      console.error('Error fetching businesses:', err);
      setError(err.message || 'Failed to fetch businesses');
      setBusinesses([]);
    } finally {
      setIsLoading(false);
    }
  }, [selectedCountry, selectedState, directoryType]);

  // UseEffect to fetch businesses when country and state change
  useEffect(() => {
    if (selectedCountry && selectedState) {
      fetchBusinesses();
    }
  }, [selectedCountry, selectedState, fetchBusinesses]);

  // Load countries from API
  useEffect(() => {
    const loadCountries = async () => {
      setIsLoading(true);
      setError(null);
      try {
        // First, run direct test functions
        await testApi();
        await testCountriesApi();

        try {
          const corsTestResponse = await fetch(API_ENDPOINTS.test);
          console.log("CORS test response status:", corsTestResponse.status);
          if (corsTestResponse.ok) {
            const corsTestData = await corsTestResponse.json();
            console.log("CORS test received data:", corsTestData);
          } else {
            console.error("CORS test failed");
          }
        } catch (corsTestError) {
          console.error("CORS test error:", corsTestError);
        }

        // Make a direct request to test the countries API
        const testUrl = 'http://localhost:8000/api/countries/';
        console.log("Testing countries API directly:", testUrl);

        // First try a direct test
        try {
          const testResponse = await fetch(testUrl);
          console.log("Direct test response status:", testResponse.status);
          if (testResponse.ok) {
            const testData = await testResponse.json();
            console.log("Direct test received data:", testData);
          } else {
            console.error("Direct test failed");
          }
        } catch (testError) {
          console.error("Direct test error:", testError);
        }

        // Now try the configured endpoint
        console.log("Fetching countries from API...");
        console.log("Countries API URL:", API_ENDPOINTS.countries);

        const response = await fetch(API_ENDPOINTS.countries);
        console.log("Countries API response status:", response.status);

        if (!response.ok) {
          const errorText = await response.text();
          console.error("Error response text:", errorText);
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Countries data received:", data);

        if (Array.isArray(data)) {
          setCountries(data);
        } else {
          console.error("Unexpected countries data format:", data);
          setError("Received invalid data format for countries");
        }
      } catch (error) {
        console.error("Error loading countries:", error);
        setError(`Failed to load countries: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadCountries();
  }, []);

  // Load states when country is selected
  useEffect(() => {
    if (!selectedCountry) {
      setStates([]);
      return;
    }

    const loadStates = async (country) => {
      if (!country) return;

      setIsLoading(true);
      setError(null);
      try {
        console.log(`Fetching states for country: ${country}`);
        const url = `${API_ENDPOINTS.states}?country=${encodeURIComponent(country)}`;
        console.log("States API URL:", url);

        const response = await fetch(url);
        console.log("States API response status:", response.status);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log("States data received:", data);

        if (Array.isArray(data)) {
          setStates(data);
        } else {
          console.error("Unexpected states data format:", data);
          setError("Received invalid data format for states");
        }
      } catch (error) {
        console.error(`Error loading states for ${country}:`, error);
        setError(`Failed to load states: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadStates(selectedCountry);
  }, [selectedCountry]);

  // Function to handle clearing filters
  const handleClearFilters = () => {
    console.log('Clearing filters');
    setSelectedCountry('');
    setSelectedState('');
    // After clearing filters, reset businesses to empty
    setBusinesses([]);
  };

  // Function to handle opening a business
  const handleOpenBusiness = (business) => {
    console.log('Opening business:', business);
    // You would typically navigate to a business details page or open an external link

    // For now, we'll just show an alert with the business name
    alert(`Opening business details for: ${business.displayName || business.BusinessName}`);
  };

  // Function to toggle business details
  const toggleBusinessDetails = (business) => {
    console.log('Showing details for business:', business);

    // Format the details to show
    const details = [
      `Business Name: ${business.displayName || business.BusinessName}`,
      business.address ? `Address: ${business.address}` : '',
      business.phone ? `Phone: ${business.phone}` : '',
      business.email ? `Email: ${business.email}` : '',
      business.website ? `Website: ${business.website}` : '',
      `Business Type: ${business.BusinessType || 'Not specified'}`
    ].filter(Boolean).join('\n');

    // Show the details in an alert (in a real app, you would show this in a modal)
    alert(details);
  };

  return (
    <div className="directories-page">
      {/* Sidebar with navigation */}
      <div className="sidebar">
        <Link to="/" className="sidebar-link">Home</Link>
        <Link to="/chat" className="sidebar-link">Chat</Link>
        <Link to="/directories" className="sidebar-link">Back to Directories</Link>
        <img src={logo} alt="Oatmeal AI" className="logo" />
      </div>

      {/* Main content */}
      <div className="main-content">
        <div className="directory-detail-container">
          <h1>{directoryType} Directory</h1>

          {/* Filter section */}
          <div className="filter-section">
            <div className="filter-row">
              <div className="filter-group">
                <label htmlFor="country-select">Country:</label>
                <select 
                  id="country-select"
                  value={selectedCountry}
                  onChange={(e) => setSelectedCountry(e.target.value)}
                >
                  <option value="">All Countries</option>
                  {countries.map((country, index) => (
                    <option key={index} value={country}>
                      {country}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <label htmlFor="state-select">State:</label>
                <select 
                  id="state-select"
                  value={selectedState}
                  onChange={(e) => setSelectedState(e.target.value)}
                  disabled={!selectedCountry}
                >
                  <option value="">All States</option>
                  {states.map((state, index) => (
                    <option key={index} value={state}>
                      {state}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="filter-actions">
              <button 
                className="filter-button clear-filter"
                onClick={handleClearFilters}
              >
                Clear Filters
              </button>
              <button 
                className={`filter-button apply-filter ${selectedCountry && selectedState ? 'active' : 'disabled'}`}
                onClick={fetchBusinesses}
                disabled={!selectedCountry || !selectedState}
              >
                Apply Filters
              </button>
            </div>
          </div>

          {/* Business Results Section */}
          <div className="businesses-container">
            <h2>{formatTitle(directoryType)} in {selectedState}, {selectedCountry}</h2>

            {isLoading ? (
              <div className="loading">Loading businesses...</div>
            ) : error ? (
              <div className="error-message">{error}</div>
            ) : businesses.length > 0 ? (
              <>
                <p className="business-count">
                  Found {businesses.length} businesses of type "{businessType}" 
                  in {selectedState}, {selectedCountry}
                </p>

                <div className="businesses-list">
                  {businesses.map(business => {
                    // Determine the display type - if it matches our directory, use the directory's type, otherwise show actual type
                    const businessActualType = business.BusinessType || business.business_type || '';
                    const displayType = 
                      businessActualType === businessType ? 
                      businessActualType : 
                      businessType;

                    return (
                      <div key={business.id} className="business-item">
                        <div className="business-content">
                          <div className="business-info">
                            <h3 className="business-name">{business.BusinessName}</h3>
                            <p className="business-type">{displayType}</p>
                            {business.Address && <p className="business-address">{business.Address}</p>}
                            {business.Phone && <p className="business-phone">{business.Phone}</p>}
                            {business.Email && <p className="business-email">{business.Email}</p>}
                            {business.Website && <p className="business-website"><a href={business.Website.startsWith('http') ? business.Website : `https://${business.Website}`} target="_blank" rel="noopener noreferrer">{business.Website}</a></p>}
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </>
            ) : (
              <div className="no-results">
                No businesses found matching your criteria. Try selecting different options.
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default DirectoryDetail;
