import React, { useState, useEffect, useRef } from 'react';
import { Navigate } from 'react-router-dom';
import { getChatHistory, sendChatMessage } from '../services/api';
import '../styles/Chat.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const messagesEndRef = useRef(null);
  const chatId = "default-chat"; // Replace with dynamic chat ID logic if needed

  // Check authentication first
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
    setLoading(false);
  }, []);

  // Fetch messages when component mounts
  useEffect(() => {
    if (!isAuthenticated) return;

    const fetchMessages = async () => {
      try {
        const data = await getChatHistory();
        setMessages(data);
      } catch (error) {
        console.error("Failed to fetch messages:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchMessages();

    // Option for real-time updates - can be replaced with WebSockets implementation
    const intervalId = setInterval(fetchMessages, 5000);
    return () => clearInterval(intervalId);
  }, [chatId, isAuthenticated]);

  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    try {
      await sendChatMessage(newMessage);
      setNewMessage('');

      // Optionally fetch messages again to refresh or update local state
      const data = await getChatHistory();
      setMessages(data);
    } catch (error) {
      console.error("Failed to send message:", error);
    }
  };

  if (loading) {
    return <div className="loading-spinner">Loading chat...</div>;
  }

  // Redirect to login if not authenticated
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>Chat Room</h2>
      </div>

      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="no-messages">No messages yet. Start a conversation!</div>
        ) : (
          messages.map((msg) => (
            <div 
              key={msg.id} 
              className={`message ${msg.isCurrentUser ? 'message-mine' : 'message-other'}`}
            >
              <div className="message-sender">{msg.sender}</div>
              <div className="message-content">{msg.content}</div>
              <div className="message-time">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSendMessage} className="message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          className="message-input"
        />
        <button type="submit" className="send-button">Send</button>
      </form>

      {/* Introduction Section */}
      <section className="w-full max-w-4xl text-center mb-12 p-6 bg-green-50 rounded-lg shadow-md">
        <h2 className="text-3xl font-bold text-green-600 mb-4">Welcome to FarmSphere.AI</h2>
        <p className="text-lg text-gray-700">
          FarmSphere.AI is your trusted farming assistant, providing solutions tailored for modern agriculture. Ask us anything, and we'll help you make informed decisions for your farm.
        </p>
      </section>

      {/* Chatbox Section */}
      <section className="w-full max-w-4xl bg-white p-6 rounded-lg shadow-md">
        <h3 className="text-2xl font-semibold text-green-600 mb-4">Ask Your Farming Questions</h3>
        <form id="query-form" className="space-y-4" onSubmit={handleSendMessage}>
          <textarea 
            id="message" 
            name="message" 
            className="w-full p-4 border-2 border-gray-300 rounded-lg shadow-sm text-lg" 
            placeholder="Type your question here..." 
            required 
            rows="5"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
          />
          <button 
            type="submit" 
            id="askAI"
            className="w-full bg-green-600 text-white py-3 rounded-lg text-lg font-semibold hover:bg-green-700 transition"
          >
            Ask
          </button>
        </form>
        <div id="response" className="mt-6 p-4 bg-green-100 border-2 border-green-600 rounded-lg text-lg text-gray-700 prose">
          {/* Chatbot response will be displayed here */}
        </div>
      </section>
    </div>
  );
}

export default Chat;
