import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { loginUser } from '../services/api';
import '../styles/Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    try {
      console.log('Attempting login with email:', email);
      
      // Use the loginUser function from api.js
      const response = await loginUser({ email, password });
      console.log('Login response received:', JSON.stringify(response));
      
      if (!response || !response.token) {
        console.error('Invalid login response:', response);
        throw new Error('Invalid login response - no token received');
      }
      
      // Store token immediately to ensure it's available for subsequent requests
      localStorage.setItem('token', response.token);
      console.log('Token stored in localStorage');
      
      // Ensure we pass email to the login function
      const loginData = {
        ...response,
        email: email // Add email to login data
      };
      
      // Use the login function from AuthContext
      await login(loginData);
      
      // Store login flags for detection
      sessionStorage.setItem('justLoggedIn', 'true');
      sessionStorage.setItem('loginTimestamp', Date.now().toString());
      
      console.log('Login successful, redirecting to directories');
      
      // Set a short timeout to ensure state is updated before redirect
      setTimeout(() => {
        // Force a full page reload to directories (most reliable way to change state)
        window.location.href = '/directories';
      }, 300); // Increased timeout for better reliability
      
    } catch (err) {
      console.error('Login error:', err);
      setError(err.message || 'Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };
  
  // Check if we've just returned from a failed redirect
  React.useEffect(() => {
    const loginFailed = sessionStorage.getItem('loginRedirectFailed');
    if (loginFailed) {
      sessionStorage.removeItem('loginRedirectFailed');
      setError('There was a problem accessing your account. Please try logging in again.');
    }
    
    // Check token to see if already logged in
    const token = localStorage.getItem('token');
    if (token) {
      console.log('Token found in localStorage, user may already be logged in');
    }
  }, []);

  return (
    <div className="login-container">
      <div className="login-form-wrapper">
        <h2>Login to Your Account</h2>
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          
          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        
        <div className="login-footer">
          <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
        </div>
      </div>
    </div>
  );
}

export default Login;
