import React, { useState, useEffect, useRef } from 'react';
import { Navigate, useNavigate } from 'react-router-dom';
import { getChatHistory, sendChatMessage, getUserProfile, checkApiEndpoints, testEndpoint } from '../services/api';
import '../styles/Chat.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('checking'); // 'checking', 'online', 'offline'
  const [retryCount, setRetryCount] = useState(0);
  const [userProfile, setUserProfile] = useState(null);
  const [diagnosticMode, setDiagnosticMode] = useState(false);
  const [diagnosticResults, setDiagnosticResults] = useState(null);
  const [authError, setAuthError] = useState(null);
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();
  const [diagnosticsRunning, setDiagnosticsRunning] = useState(false);
  const [diagnosticsResults, setDiagnosticsResults] = useState(null);
  const [showAuthError, setShowAuthError] = useState(false);
  const [authErrorMessage, setAuthErrorMessage] = useState('');
  
  // Handle user logout on authentication errors
  const handleLogout = () => {
    console.log('Logging out due to authentication error...');
    console.log('Previous token debug info:', getTokenDebugInfo());
    
    // Clear all auth and user data
    localStorage.removeItem('token');
    console.log('Cleared token from localStorage');
    
    // Clear all session storage
    sessionStorage.removeItem('lastLoginEmail');
    sessionStorage.removeItem('userName');
    sessionStorage.removeItem('userId');
    console.log('Cleared user data from sessionStorage');
    
    // Clear any user data in state
    setUserProfile(null);
    setIsAuthenticated(false);
    setAuthError(null);
    console.log('Reset user state');
    
    console.log('All user data cleared, redirecting to login');
    
    // Redirect to login page after a brief delay
    setTimeout(() => {
      navigate('/login');
    }, 3000);
  };
  
  // Check authentication first
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      
      // Check if token appears to be JWT format (starts with eyJ)
      const isJWT = token.startsWith('eyJ');
      console.log('Token appears to be JWT format:', isJWT);
      
      // Try to get user profile
      getUserProfile()
        .then(profile => {
          console.log('Loaded user profile:', profile);
          setUserProfile(profile);
          
          // Store user name in session storage with improved fallbacks
          if (profile.name) {
            sessionStorage.setItem('userName', profile.name);
          } else if (profile.username) {
            sessionStorage.setItem('userName', profile.username);
          } else if (profile.email) {
            // Extract username from email for better display
            const username = profile.email.split('@')[0];
            // Capitalize first letter for better presentation
            const displayName = username.charAt(0).toUpperCase() + username.slice(1);
            sessionStorage.setItem('userName', displayName);
          }
          
          // Update email info if available
          if (profile.email) {
            sessionStorage.setItem('lastLoginEmail', profile.email);
          }
          
          setAuthError(null); // Clear any auth errors
          
          // Check connection to backend after profile is loaded
          testBackendConnection();
        })
        .catch(err => {
          console.error('Failed to load profile:', err);
          
          // Check if this is an authentication error or just a missing endpoint
          const isAuthError = err.message === 'Authentication required' || 
                              err.message === 'Session expired. Please log in again.' ||
                              err.message === 'Authentication failed. Please log in again.' ||
                              (err.response && (err.response.status === 401 || err.response.status === 403));
          
          if (isAuthError) {
            // Only logout if it's a true authentication error
            setAuthError(err.message || 'Authentication error');
            // Delay logout to allow error to be displayed
            setTimeout(() => handleLogout(), 3000);
          } else {
            // For 404 errors or network issues, use fallback data and continue
            console.log('Using fallback profile data due to endpoint not found (404)');
            const email = sessionStorage.getItem('lastLoginEmail');
            const name = sessionStorage.getItem('userName');
            
            if (email || name) {
              setUserProfile({
                email: email || '',
                name: name || 'User',
                profile_picture: null
              });
              
              // Check connection to backend even when using fallback profile
              testBackendConnection();
            } else {
              // If we have no fallback data at all, logout as a last resort
              setAuthError('No profile data available. Please login again.');
              setTimeout(() => handleLogout(), 3000);
            }
          }
        });
    } else {
      setIsAuthenticated(false);
    }
    setLoading(false);
  }, []);
  
  // Add a function to debug token
  const getTokenDebugInfo = () => {
    const token = localStorage.getItem('token');
    if (!token) return 'No token found';
    
    return `Token length: ${token.length}, format: ${token.startsWith('eyJ') ? 'JWT' : 'Simple token'}`;
  };
  
  // Test connectivity to backend
  const testBackendConnection = async () => {
    console.log('Testing backend connection...');
    console.log('Token debug info:', getTokenDebugInfo());
    
    try {
      const result = await sendChatMessage('__test_connection__');
      
      if (!result.success) {
        if (result.authError) {
          console.log('Authentication error during connection test:', result.message);
          setConnectionStatus('auth_error');
          setShowAuthError(true);
          setAuthErrorMessage(result.message || 'Authentication failed');
          
          // Delay logout to show error message
          setTimeout(() => {
            handleLogout();
          }, 5000); // 5 seconds delay to allow user to see diagnostics
          return;
        }
        
        if (result.serverRunning) {
          // Server is running but API endpoint not found (404)
          console.log('Server running but API endpoint not found');
          setConnectionStatus('offline');
          setConnected(false);
          // Don't trigger auth error for 404s
          return;
        }
        
        console.log('Connection test failed:', result.error);
        setConnectionStatus('offline');
        setConnected(false);
      } else {
        console.log('Connection test successful');
        setConnectionStatus('online');
        setConnected(true);
      }
    } catch (error) {
      console.error('Error testing connection:', error);
      setConnectionStatus('offline');
      setConnected(false);
    }
  };

  // Function to run API diagnostics
  const runApiDiagnostics = async () => {
    setDiagnosticsRunning(true);
    setDiagnosticsResults(null);
    
    try {
      const endpoints = [
        { name: 'Root endpoint', path: '/' },
        { name: 'Admin panel', path: '/admin/' },
        { name: 'API root', path: '/api/' },
        { name: 'API V1 root', path: '/apiV1/' },
        { name: 'Chat endpoint (API)', path: '/api/chat/' },
        { name: 'Chat endpoint (API V1)', path: '/apiV1/chat/' },
        { name: 'Ping endpoint (API)', path: '/api/ping/' },
        { name: 'Ping endpoint (API V1)', path: '/apiV1/ping/' },
        { name: 'Health check', path: '/api/health/' },
        { name: 'User profile (API)', path: '/api/user/profile/' },
        { name: 'User endpoint (API)', path: '/api/user/' },
        { name: 'Profile endpoint (API)', path: '/api/profile/' },
        { name: 'Me endpoint (API)', path: '/api/me/' }
      ];
      
      const results = [];
      
      for (const endpoint of endpoints) {
        try {
          const response = await testEndpoint(endpoint.path);
          results.push({
            ...endpoint,
            status: response.status,
            success: response.status >= 200 && response.status < 300,
            authError: response.status === 401 || response.status === 403,
            notFound: response.status === 404,
            message: response.message || `Status: ${response.status}`,
            data: response.data
          });
        } catch (error) {
          results.push({
            ...endpoint,
            status: error.response?.status || 'Error',
            success: false,
            error: error.message,
            message: `Error: ${error.message}`
          });
        }
      }
      
      setDiagnosticsResults(results);
      console.log('API Diagnostics Results:', results);
    } catch (error) {
      console.error('Error running API diagnostics:', error);
      setDiagnosticsResults([{
        name: 'Diagnostics',
        status: 'Error',
        success: false,
        error: error.message,
        message: `Failed to run diagnostics: ${error.message}`
      }]);
    } finally {
      setDiagnosticsRunning(false);
    }
  };

  // Fetch messages when component mounts
  useEffect(() => {
    if (!isAuthenticated) return;

    const fetchMessages = async () => {
      try {
        setLoading(true);
        const data = await getChatHistory();
        if (Array.isArray(data) && data.length > 0) {
          setMessages(data);
          // If we got history, we're online
          setConnectionStatus('online');
        } else if (messages.length === 0) {
          // Add a welcome message if we don't have any messages
          setMessages([{
            id: Date.now(),
            content: "Hello! I'm the AI Assistant powered by Azure OpenAI. How can I help you today?",
            sender: 'Azure AI',
            timestamp: new Date().toISOString(),
            isCurrentUser: false
          }]);
          
          // Try to connect to backend to set proper status
          await testBackendConnection();
        }
      } catch (error) {
        console.error("Failed to fetch messages:", error);
        
        // If we've tried less than 3 times, attempt to test connectivity again
        if (retryCount < 3) {
          setRetryCount(prevCount => prevCount + 1);
          await testBackendConnection();
        } else {
          setConnectionStatus('offline');
        }
        
        // If no messages are present, add a welcome message
        if (messages.length === 0) {
          setMessages([{
            id: Date.now(),
            content: "Hello! I'm the AI Assistant powered by Azure OpenAI. I might be operating in offline mode, but I'll do my best to assist you.",
            sender: 'Azure AI',
            timestamp: new Date().toISOString(),
            isCurrentUser: false
          }]);
        }
      } finally {
        setLoading(false);
      }
    };
    
    fetchMessages();
    
    // We'll also explicitly test the connection
    const checkConnection = async () => {
      await testBackendConnection();
    };
    
    // Check connection every 30 seconds
    const intervalId = setInterval(checkConnection, 30000); 
    
    // Initial connection check
    checkConnection();
    
    return () => clearInterval(intervalId);
  }, [isAuthenticated, retryCount]);
  
  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);
  
  // Send a message to the AI
  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;
    
    // Add user message to chat
    const userMessage = {
      id: Date.now(),
      content: inputMessage,
      sender: userProfile?.name || sessionStorage.getItem('userName') || 'You',
      timestamp: new Date().toISOString(),
      isCurrentUser: true
    };
    
    setMessages(prevMessages => [...prevMessages, userMessage]);
    setInputMessage('');
    setIsProcessing(true);
    
    // Add temporary "thinking" message
    const thinkingId = Date.now() + 1;
    setMessages(prevMessages => [
      ...prevMessages, 
      {
        id: thinkingId,
        content: '...',
        sender: 'Azure AI',
        timestamp: new Date().toISOString(),
        isCurrentUser: false,
        isThinking: true
      }
    ]);
    
    try {
      // Send to backend
      const response = await sendChatMessage(inputMessage);
      
      // Remove temporary thinking message
      setMessages(prevMessages => 
        prevMessages.filter(msg => msg.id !== thinkingId)
      );
      
      if (response.success) {
        // Add AI response to chat
        const aiMessage = {
          id: Date.now() + 2,
          content: response.ai_response,
          sender: 'Azure AI',
          timestamp: new Date().toISOString(),
          isCurrentUser: false,
          sources: response.sources
        };
        setMessages(prevMessages => [...prevMessages, aiMessage]);
        setConnectionStatus('online');
      } else if (response.authError) {
        // Handle authentication errors
        setAuthError(`Authentication error: ${response.message}`);
        setTimeout(() => handleLogout(), 3000);
      } else {
        // Handle other errors
        const errorMessage = {
          id: Date.now() + 2,
          content: "I'm having trouble connecting to the server. Please try again later.",
          sender: 'Azure AI',
          timestamp: new Date().toISOString(),
          isCurrentUser: false
        };
        setMessages(prevMessages => [...prevMessages, errorMessage]);
        setConnectionStatus('offline');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      
      // Remove temporary thinking message
      setMessages(prevMessages => 
        prevMessages.filter(msg => msg.id !== thinkingId)
      );
      
      // Add error message
      const errorMessage = {
        id: Date.now() + 2,
        content: "An error occurred while processing your request. Please try again.",
        sender: 'Azure AI',
        timestamp: new Date().toISOString(),
        isCurrentUser: false
      };
      setMessages(prevMessages => [...prevMessages, errorMessage]);
      setConnectionStatus('offline');
      
      // Handle auth errors
      if (error.response && (error.response.status === 401 || error.response.status === 403)) {
        setAuthError('Authentication error: ' + (error.response.data?.detail || 'Invalid token'));
        setTimeout(() => handleLogout(), 3000);
      }
    } finally {
      setIsProcessing(false);
    }
  };
  
  // Check JWT token format and validity
  const checkJwtToken = () => {
    const token = localStorage.getItem('token');
    
    if (!token) {
      console.error('No token found in localStorage');
      return {
        present: false,
        message: 'No authentication token found'
      };
    }
    
    console.log('Token found, length:', token.length);
    
    // Check if it looks like a JWT (starts with eyJ which is 'ey' in base64)
    const isJwt = token.startsWith('eyJ');
    console.log('Token appears to be JWT:', isJwt);
    
    if (isJwt) {
      // Try to decode the JWT to verify format (not signature)
      try {
        // JWT has 3 parts separated by dots
        const parts = token.split('.');
        if (parts.length !== 3) {
          console.error('JWT format invalid: wrong number of segments');
          return {
            present: true,
            valid: false,
            format: 'JWT',
            message: 'JWT format invalid: wrong number of segments'
          };
        }
        
        // Try to decode the header and payload
        const header = JSON.parse(atob(parts[0]));
        const payload = JSON.parse(atob(parts[1]));
        
        console.log('JWT header:', header);
        console.log('JWT payload:', payload);
        
        // Check for expiration
        if (payload.exp) {
          const expiryDate = new Date(payload.exp * 1000);
          const now = new Date();
          const isExpired = expiryDate < now;
          
          console.log('JWT expiry date:', expiryDate.toISOString());
          console.log('JWT is expired:', isExpired);
          
          if (isExpired) {
            return {
              present: true,
              valid: false,
              format: 'JWT',
              expired: true,
              message: `JWT token expired at ${expiryDate.toLocaleString()}`
            };
          }
        }
        
        return {
          present: true,
          valid: true,
          format: 'JWT',
          algorithm: header.alg,
          type: header.typ,
          message: 'JWT token appears to be valid'
        };
      } catch (error) {
        console.error('Error decoding JWT:', error);
        return {
          present: true,
          valid: false,
          format: 'JWT',
          message: `Error decoding JWT: ${error.message}`
        };
      }
    } else {
      // Handle non-JWT token
      return {
        present: true,
        format: 'non-JWT',
        message: 'Token is not in JWT format'
      };
    }
  };
  
  if (loading) {
    return <div className="loading-spinner">Loading chat...</div>;
  }

  // Redirect to login if not authenticated
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }
  
  // Get display name - first from profile, then session, then fallback
  const displayName = 
    (userProfile && (userProfile.name || userProfile.username)) || 
    sessionStorage.getItem('userName') ||
    sessionStorage.getItem('lastLoginEmail') || 
    'User';
    
  const firstName = displayName.split(' ')[0];
  
  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>Chat with Azure AI</h2>
        <div className="connection-status">
          {connectionStatus === 'online' ? (
            <span className="status-online"> Azure Online</span>
          ) : connectionStatus === 'offline' ? (
            <div className="offline-notice">
              <span className="status-offline"> Azure Offline</span>
              <button 
                className="retry-button" 
                onClick={testBackendConnection}
                disabled={connectionStatus === 'checking'}
              >
                {connectionStatus === 'checking' ? 'Checking...' : 'Retry Connection'}
              </button>
            </div>
          ) : (
            <span className="status-checking"> Checking Connection...</span>
          )}
        </div>
      </div>
      
      {showAuthError && (
        <div className="auth-error-notice">
          <div className="error-title">
            <h3>Authentication Error: {authErrorMessage}</h3>
          </div>
          <p>Check that your Django server is properly configured to accept the token format being sent.</p>
          <p className="token-info">
            {getTokenDebugInfo()}
          </p>
          <p>
            <button 
              className="diagnostic-button" 
              onClick={() => {
                const jwtInfo = checkJwtToken();
                console.log('JWT Diagnostic Results:', jwtInfo);
                setAuthErrorMessage(jwtInfo.message || 'Authentication failed');
                // Show JWT diagnostic details
                const detailsElement = document.createElement('div');
                detailsElement.className = 'jwt-details';
                detailsElement.innerHTML = `
                  <h4>JWT Token Diagnostic:</h4>
                  <pre>${JSON.stringify(jwtInfo, null, 2)}</pre>
                `;
                // Find the auth error notice and append or replace details
                const errorNotice = document.querySelector('.auth-error-notice');
                const existingDetails = errorNotice.querySelector('.jwt-details');
                if (existingDetails) {
                  errorNotice.replaceChild(detailsElement, existingDetails);
                } else {
                  errorNotice.appendChild(detailsElement);
                }
              }}
            >
              Check JWT Token
            </button>
            <button 
              className="diagnostic-button" 
              onClick={() => {
                // Try sending the token without any prefix
                localStorage.setItem('tokenSendFormat', 'direct');
                testBackendConnection();
              }}
            >
              Try Direct Token Format
            </button>
          </p>
          <p>You will be redirected to login in a moment...</p>
        </div>
      )}
      
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="no-messages">
            <p>Hello {firstName}, welcome to the AI Assistant!</p>
            <p className="hint">Try asking: "What can this application do for me?" or "Tell me about this project"</p>
          </div>
        ) : (
          messages.map((msg) => (
            <div 
              key={msg.id} 
              className={`message ${msg.isCurrentUser ? 'message-mine' : 'message-other'} ${msg.isThinking ? 'thinking' : ''} ${msg.isDiagnostic ? 'diagnostic' : ''}`}
            >
              <div className="message-sender">
                {msg.isCurrentUser 
                  ? (userProfile?.name || sessionStorage.getItem('userName') || displayName)
                  : msg.sender}
              </div>
              <div className="message-content">
                {msg.isThinking ? (
                  <div className="thinking-indicator">
                    <span>Thinking</span>
                    <span className="dot">.</span>
                    <span className="dot">.</span>
                    <span className="dot">.</span>
                  </div>
                ) : (
                  msg.content
                )}
                {msg.sources && msg.sources.length > 0 && (
                  <div className="message-sources">
                    <p className="sources-title">Sources:</p>
                    <ul>
                      {msg.sources.map((source, index) => (
                        <li key={index}>
                          {source.title || source.url || source.path || "Reference document"}
                          {source.url && <a href={source.url} target="_blank" rel="noopener noreferrer"> (link)</a>}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
              <div className="message-time">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>
      
      <form onSubmit={handleSendMessage} className="message-form">
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Ask me anything..."
          className="message-input"
          disabled={isProcessing || connectionStatus === 'offline' || authError}
        />
        <button 
          type="submit" 
          className={`send-button ${isProcessing ? 'disabled' : ''}`}
          disabled={isProcessing || !inputMessage.trim() || connectionStatus === 'offline' || authError}
        >
          {isProcessing ? 'Sending...' : 'Send'}
        </button>
      </form>
      
      {connectionStatus === 'offline' && (
        <div className="offline-notice">
          <p><strong>Disconnected from Azure OpenAI Services</strong></p>
          <p>Possible reasons:</p>
          <ul className="offline-reasons">
            <li>The RAG pipeline API endpoint is not available on the server</li>
            <li>The Django server might not have the expected chat endpoint configured</li>
            <li>Your authentication token might not have permission to access the RAG service</li>
          </ul>
          
          <div className="offline-actions">
            <button 
              onClick={testBackendConnection} 
              disabled={isProcessing}
              className="btn-secondary"
            >
              Retry Azure Connection
            </button>
            <button 
              onClick={runApiDiagnostics} 
              disabled={diagnosticsRunning}
              className="btn-primary"
            >
              Run API Diagnostics
            </button>
          </div>
          
          {diagnosticsRunning && <p>Running API diagnostics...</p>}
          
          {diagnosticsResults && (
            <div className="diagnostics-results">
              <h4>API Diagnostics Results:</h4>
              <div className="results-table">
                <table>
                  <thead>
                    <tr>
                      <th>Endpoint</th>
                      <th>Path</th>
                      <th>Status</th>
                      <th>Result</th>
                    </tr>
                  </thead>
                  <tbody>
                    {diagnosticsResults.map((result, index) => (
                      <tr key={index} className={result.success ? 'success' : result.notFound ? 'warning' : 'error'}>
                        <td>{result.name}</td>
                        <td>{result.path}</td>
                        <td>{result.status}</td>
                        <td>{result.message}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
          
          <div className="troubleshooting-tips">
            <h4>Troubleshooting Tips:</h4>
            <ol>
              <li>Ensure your Django backend is running at http://127.0.0.1:8000</li>
              <li>Check your Azure OpenAI service and API key configuration in Django settings</li>
              <li>Look for detailed error messages in your browser console</li>
              <li>Verify that your Azure API keys are valid and have the necessary permissions</li>
              <li>Check that your Django backend has the expected API routes configured</li>
            </ol>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chat;
