import React, { useState, useEffect, useContext } from 'react';
import { Link, Navigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { getDirectories } from '../services/api';
import '../styles/Directories.css';

function Directories() {
  const [directories, setDirectories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isUserAuthenticated, setIsUserAuthenticated] = useState(false);
  const { user } = useContext(AuthContext);

  // Check authentication status
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    
    console.log('Directories - Auth check: Token exists:', !!token);
    console.log('Directories - Auth check: User data exists:', !!storedUser);
    console.log('Directories - Auth check: Context user exists:', !!user);
    
    // Consider authenticated if token exists
    if (token) {
      setIsUserAuthenticated(true);
    } else {
      console.log('Directories - Not authenticated, redirecting to login');
      setIsUserAuthenticated(false);
    }
    
    setLoading(false);
  }, [user]);

  // Fetch directories when component mounts
  useEffect(() => {
    if (isUserAuthenticated) {
      fetchDirectories();
    }
  }, [isUserAuthenticated]);

  // Function to fetch directories from the API
  const fetchDirectories = async () => {
    try {
      console.log('Fetching directories...');
      const data = await getDirectories();
      console.log('Directories fetched:', data);
      setDirectories(data);
      setError(null);
    } catch (err) {
      console.error('Error fetching directories:', err);
      setError('Failed to load directories. Please try again.');
      // Still show some mock directories even if there's an error
      setDirectories([
        { id: 1, name: 'Documents', path: '/documents', color: '#4285F4' },
        { id: 2, name: 'Images', path: '/images', color: '#34A853' },
        { id: 3, name: 'Projects', path: '/projects', color: '#FBBC05' },
        { id: 4, name: 'Templates', path: '/templates', color: '#EA4335' }
      ]);
    } finally {
      setLoading(false);
    }
  };

  // Get user's display name
  const getUserDisplayName = () => {
    try {
      // Try to get name from context first
      if (user && (user.name || user.fullName || user.email)) {
        return user.name || user.fullName || user.email;
      }
      
      // If not in context, try localStorage
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        const userData = JSON.parse(storedUser);
        return userData.name || userData.fullName || userData.email || 'User';
      }
      
      return 'User';
    } catch (error) {
      console.error('Error getting user display name:', error);
      return 'User';
    }
  };

  // Show loading state
  if (loading) {
    return <div className="loading-container">Loading...</div>;
  }

  // Redirect to login if not authenticated
  if (!isUserAuthenticated) {
    return <Navigate to="/login" />;
  }

  return (
    <div className="directories-container">
      <header className="directories-header">
        <h1>Welcome to Oatmeal AI Directories</h1>
        <p>Hello, {getUserDisplayName()}! Here are your available directories.</p>
      </header>

      {error && <div className="error-message">{error}</div>}

      <div className="directories-grid">
        {directories.map((directory) => (
          <Link
            to={`/directory${directory.path}`}
            className="directory-card"
            key={directory.id}
            style={{ borderColor: directory.color || '#4285F4' }}
          >
            <div 
              className="directory-icon"
              style={{ backgroundColor: directory.color || '#4285F4' }}
            >
              <i className="fas fa-folder"></i>
            </div>
            <h3>{directory.name}</h3>
            <p className="directory-path">{directory.path}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Directories;
