import React, { useState, useEffect, useRef } from 'react';
import { Navigate } from 'react-router-dom';
import { getChatHistory, sendChatMessage } from '../services/api';
import '../styles/Chat.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('checking'); // 'checking', 'online', 'offline'
  const messagesEndRef = useRef(null);
  const chatId = "default-chat";
  
  // Check authentication first
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
    setLoading(false);
  }, []);

  // Fetch messages when component mounts
  useEffect(() => {
    if (!isAuthenticated) return;

    const fetchMessages = async () => {
      try {
        setLoading(true);
        const data = await getChatHistory();
        if (Array.isArray(data) && data.length > 0) {
          setMessages(data);
          setConnectionStatus('online');
        } else if (messages.length === 0) {
          // Add a welcome message if we don't have any messages
          setMessages([{
            id: Date.now(),
            content: "Hello! I'm the OatmealAI Assistant. How can I help you today?",
            sender: 'OatmealAI',
            timestamp: new Date().toISOString(),
            isCurrentUser: false
          }]);
          setConnectionStatus('offline');
        }
      } catch (error) {
        console.error("Failed to fetch messages:", error);
        setConnectionStatus('offline');
        // If no messages are present, add a welcome message
        if (messages.length === 0) {
          setMessages([{
            id: Date.now(),
            content: "Hello! I'm the OatmealAI Assistant. I might be operating in offline mode, but I'll do my best to assist you.",
            sender: 'OatmealAI',
            timestamp: new Date().toISOString(),
            isCurrentUser: false
          }]);
        }
      } finally {
        setLoading(false);
      }
    };
    
    fetchMessages();
    
    // Option for real-time updates - can be replaced with WebSockets implementation
    const intervalId = setInterval(fetchMessages, 30000); // Reduced polling frequency to 30 seconds
    return () => clearInterval(intervalId);
  }, [chatId, isAuthenticated]);
  
  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);
  
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || isProcessing) return;
    
    try {
      setIsProcessing(true);
      
      // Add user message to UI immediately
      const userMessageContent = newMessage.trim();
      const userMessage = {
        id: Date.now(),
        content: userMessageContent,
        sender: 'User',
        timestamp: new Date().toISOString(),
        isCurrentUser: true
      };
      
      setMessages(prevMessages => [...prevMessages, userMessage]);
      setNewMessage('');
      
      // Add temporary "thinking" message
      const thinkingMessage = {
        id: Date.now() + 1,
        content: "Thinking...",
        sender: 'OatmealAI',
        timestamp: new Date().toISOString(),
        isCurrentUser: false,
        isThinking: true
      };
      
      setMessages(prevMessages => [...prevMessages, thinkingMessage]);
      
      // Send message to RAG pipeline
      const response = await sendChatMessage(userMessageContent);
      
      // Replace thinking message with actual response
      setMessages(prevMessages => {
        const filteredMessages = prevMessages.filter(msg => !msg.isThinking);
        return [
          ...filteredMessages, 
          response.aiResponse
        ];
      });
      
      // If we got a successful response, we're online
      setConnectionStatus('online');
      
    } catch (error) {
      console.error("Failed to process message:", error);
      setConnectionStatus('offline');
      
      // Add error message
      const errorMessage = {
        id: Date.now() + 2,
        content: "Sorry, I couldn't process your request. I might be operating in offline mode at the moment. Please try again later.",
        sender: 'OatmealAI',
        timestamp: new Date().toISOString(),
        isCurrentUser: false
      };
      
      setMessages(prevMessages => {
        const filteredMessages = prevMessages.filter(msg => !msg.isThinking);
        return [...filteredMessages, errorMessage];
      });
      
    } finally {
      setIsProcessing(false);
    }
  };
  
  if (loading) {
    return <div className="loading-spinner">Loading chat...</div>;
  }

  // Redirect to login if not authenticated
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }
  
  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>OatmealAI Assistant</h2>
        {connectionStatus !== 'checking' && (
          <div className={`connection-status ${connectionStatus}`}>
            {connectionStatus === 'online' ? 'Connected' : 'Offline Mode'}
          </div>
        )}
      </div>
      
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="no-messages">
            <p>No messages yet. Ask me anything about our services!</p>
            <p className="hint">Try asking: "What can OatmealAI do for me?" or "How do I use this application?"</p>
          </div>
        ) : (
          messages.map((msg) => (
            <div 
              key={msg.id} 
              className={`message ${msg.isCurrentUser ? 'message-mine' : 'message-other'} ${msg.isThinking ? 'thinking' : ''}`}
            >
              <div className="message-sender">{msg.sender}</div>
              <div className="message-content">
                {msg.content}
                {msg.sources && msg.sources.length > 0 && (
                  <div className="message-sources">
                    <p className="sources-title">Sources:</p>
                    <ul>
                      {msg.sources.map((source, index) => (
                        <li key={index}>
                          {source.title || source.url || source.path || "Reference document"}
                          {source.url && <a href={source.url} target="_blank" rel="noopener noreferrer"> (link)</a>}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
              <div className="message-time">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>
      
      <form onSubmit={handleSendMessage} className="message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Ask me anything..."
          className="message-input"
          disabled={isProcessing}
        />
        <button 
          type="submit" 
          className={`send-button ${isProcessing ? 'disabled' : ''}`}
          disabled={isProcessing}
        >
          {isProcessing ? 'Sending...' : 'Send'}
        </button>
      </form>
      
      {connectionStatus === 'offline' && (
        <div className="offline-notice">
          <p>You're currently in offline mode. The assistant will provide general responses without accessing the full knowledge base.</p>
        </div>
      )}
    </div>
  );
}

export default Chat;
