import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/DirectoryDetail.css';
import logo from '../images/OatmealaLogoi (1).png';
import { API_ENDPOINTS } from '../config';
import { testApi, testCountriesApi } from '../test-api';

// Import images with the correct file extensions
import agriAssociaImg from '../images/agri_associa.png';
import artisianImg from '../images/artisian.jpg';
import brImg from '../images/br.jpg';
import crafOrgImg from '../images/craf_org.jpg';
import farmersMarketImg from '../images/farmers_market.jpg';
import farmsRanchesImg from '../images/farms_ranches.jpg';
import fiberImg from '../images/fiber.jpg';
import fiberMillsImg from '../images/fiber_mills.jpg';
import fisheriesImg from '../images/fisheries.jpeg';
import fishermenImg from '../images/fishermen.jpeg';
import foodCopImg from '../images/food_cop.jpg';
import foodHubImg from '../images/food_hub.jpg';
import groceryStoreImg from '../images/grocery_store.jpeg';
import manfacImg from '../images/manfac.jpeg';
import marinasImg from '../images/marinas.jpeg';
import meatImg from '../images/meat.jpg';
import realEstateImg from '../images/real_estate.webp';
import restaurantsImg from '../images/restaurants.jpg';
import retailersImg from '../images/retailers.png';
import serviceProvidersImg from '../images/service_providers.webp';
import universitiesImg from '../images/universities.jpeg';
import vetImg from '../images/vet.webp';
import vineyardsImg from '../images/vineyards.jpeg';
import wineriesImg from '../images/wineries.png';
import othersImg from '../images/others.jpg';

function DirectoryDetail() {
  const { directoryType } = useParams();
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [businesses, setBusinesses] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedState, setSelectedState] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [expandedBusinessId, setExpandedBusinessId] = useState(null);

  // Format the directory type for display (convert path slugs to title)
  const formatTitle = (slug) => {
    if (!slug) return '';
    return slug.split('-')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const title = formatTitle(directoryType);

  // Map of directory type slugs to their corresponding images
  const directoryImages = {
    'agricultural-associations': agriAssociaImg,
    'artisan-producers': artisianImg,
    'business-resources': brImg,
    'crafter-organizations': crafOrgImg,
    'farmers-markets': farmersMarketImg,
    'farms-ranches': farmsRanchesImg,
    'fiber-cooperatives': fiberImg,
    'fiber-mills': fiberMillsImg,
    'fisheries': fisheriesImg,
    'fishermen': fishermenImg,
    'food-cooperatives': foodCopImg,
    'food-hubs': foodHubImg,
    'grocery-stores': groceryStoreImg,
    'manufacturers': manfacImg,
    'marinas': marinasImg,
    'meat-wholesalers': meatImg,
    'real-estate-agents': realEstateImg,
    'restaurants': restaurantsImg,
    'retailers': retailersImg,
    'service-providers': serviceProvidersImg,
    'universities': universitiesImg,
    'veterinarians': vetImg,
    'vineyards': vineyardsImg,
    'wineries': wineriesImg,
    'others': othersImg
  };

  // Get the correct image for the current directory type
  const getHeaderImage = () => {
    return directoryImages[directoryType] || othersImg; // Default to "others" if not found
  };

  // Load countries from API
  useEffect(() => {
    const loadCountries = async () => {
      setIsLoading(true);
      setError(null);
      try {
        // First, run direct test functions
        await testApi();
        await testCountriesApi();
        
        try {
          const corsTestResponse = await fetch(API_ENDPOINTS.test);
          console.log("CORS test response status:", corsTestResponse.status);
          if (corsTestResponse.ok) {
            const corsTestData = await corsTestResponse.json();
            console.log("CORS test received data:", corsTestData);
          } else {
            console.error("CORS test failed");
          }
        } catch (corsTestError) {
          console.error("CORS test error:", corsTestError);
        }
        
        // Make a direct request to test the countries API
        const testUrl = 'http://localhost:8000/api/countries/';
        console.log("Testing countries API directly:", testUrl);
        
        // First try a direct test
        try {
          const testResponse = await fetch(testUrl);
          console.log("Direct test response status:", testResponse.status);
          if (testResponse.ok) {
            const testData = await testResponse.json();
            console.log("Direct test received data:", testData);
          } else {
            console.error("Direct test failed");
          }
        } catch (testError) {
          console.error("Direct test error:", testError);
        }
        
        // Now try the configured endpoint
        console.log("Fetching countries from API...");
        console.log("Countries API URL:", API_ENDPOINTS.countries);
        
        const response = await fetch(API_ENDPOINTS.countries);
        console.log("Countries API response status:", response.status);
        
        if (!response.ok) {
          const errorText = await response.text();
          console.error("Error response text:", errorText);
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        console.log("Countries data received:", data);
        
        if (Array.isArray(data)) {
          setCountries(data);
        } else {
          console.error("Unexpected countries data format:", data);
          setError("Received invalid data format for countries");
        }
      } catch (error) {
        console.error("Error loading countries:", error);
        setError(`Failed to load countries: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadCountries();
  }, []);

  // Load states when country is selected
  useEffect(() => {
    if (!selectedCountry) {
      setStates([]);
      return;
    }

    const loadStates = async (country) => {
      if (!country) return;
      
      setIsLoading(true);
      setError(null);
      try {
        console.log(`Fetching states for country: ${country}`);
        const url = `${API_ENDPOINTS.states}?country=${encodeURIComponent(country)}`;
        console.log("States API URL:", url);
        
        const response = await fetch(url);
        console.log("States API response status:", response.status);
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        console.log("States data received:", data);
        
        if (Array.isArray(data)) {
          setStates(data);
        } else {
          console.error("Unexpected states data format:", data);
          setError("Received invalid data format for states");
        }
      } catch (error) {
        console.error(`Error loading states for ${country}:`, error);
        setError(`Failed to load states: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadStates(selectedCountry);
  }, [selectedCountry]);

  // Load businesses when country and state are selected
  useEffect(() => {
    if (!selectedCountry || !selectedState) {
      setBusinesses([]);
      return;
    }

    const loadBusinesses = async () => {
      if (!selectedCountry || !selectedState) {
        console.log("Country or state not selected, not loading businesses");
        return;
      }
      
      setIsLoading(true);
      setError(null);
      
      try {
        // Use directoryType for the API call
        const encodedDirectory = encodeURIComponent(directoryType);
        const encodedCountry = encodeURIComponent(selectedCountry);
        const encodedState = encodeURIComponent(selectedState);
        
        // Use the full URL with base URL from config
        const url = `http://localhost:8000/api/directory-businesses/?directory_type=${encodedDirectory}&country=${encodedCountry}&state=${encodedState}`;
        console.log(`Fetching businesses from: ${url}`);
        
        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });
        
        console.log(`Response status: ${response.status}`);
        console.log('Response headers:', Object.fromEntries([...response.headers]));
        
        if (!response.ok) {
          const errorText = await response.text();
          console.error('Error response body:', errorText);
          
          if (response.status === 404) {
            throw new Error(`API endpoint not found. Please check if the backend server is running.`);
          } else {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
        }
        
        const data = await response.json();
        console.log('Businesses data:', data);
        
        // Check if data is an array and has the expected structure
        if (Array.isArray(data) && data.length > 0) {
          // Log the first item to see its structure
          console.log('First business item structure:', data[0]);
          
          // Ensure each business has a name property by checking various possible name fields
          const processedData = data.map(business => {
            // Check for different possible name fields in the data
            const businessName = 
              business.name || 
              business.Name || 
              business.business_name || 
              business.BusinessName || 
              business.company_name || 
              business.CompanyName || 
              business.organization_name || 
              business.OrganizationName ||
              `Business ${business.id || business.ID || 'Unknown'}`;
            
            return {
              ...business,
              name: businessName
            };
          });
          
          console.log('Processed data with names:', processedData);
          setBusinesses(processedData);
        } else {
          console.warn('Received empty or invalid data format:', data);
          throw new Error('No businesses found or invalid data format received');
        }
      } catch (error) {
        console.error('Error loading businesses:', error);
        setError(error.message);
        
        // For development/testing - create mock data if API fails
        console.log('Using mock data since API failed');
        
        // Generate 10 mock businesses
        const mockBusinesses = Array.from({ length: 10 }, (_, index) => ({
          id: index + 1,
          name: [
            'America Angora Goat Breeders Association',
            'American Boar Goat Association',
            'California Dairy Association',
            'Tech Industry Alliance',
            'Farmers Market Cooperative',
            'Small Business Network',
            'Organic Farmers Coalition',
            'Digital Marketing Group',
            'Healthcare Providers Association',
            'Restaurant Owners Network'
          ][index] || `Business ${index + 1}`
        }));
        
        setBusinesses(mockBusinesses);
      } finally {
        setIsLoading(false);
      }
    };

    loadBusinesses();
  }, [selectedCountry, selectedState, directoryType]);

  // Clear all selections
  const clearAll = () => {
    setSelectedCountry('');
    setSelectedState('');
    setBusinesses([]);
  };

  // Debug location
  const debugLocation = async () => {
    if (!selectedCountry || !selectedState) {
      alert('Please select both country and state first');
      return;
    }

    try {
      setIsLoading(true);
      const response = await fetch(`/api/location-info/${encodeURIComponent(selectedCountry)}/${encodeURIComponent(selectedState)}`);
      
      if (!response.ok) {
        throw new Error('Failed to fetch location info');
      }
      
      // Try to parse the response as JSON
      let data;
      try {
        const responseText = await response.text();
        console.log("Raw debug response:", responseText);
        data = JSON.parse(responseText);
      } catch (parseError) {
        console.error("JSON parse error in debugLocation:", parseError);
        throw new Error(`Failed to parse debug response: ${parseError.message}`);
      }
      
      alert(JSON.stringify(data, null, 2));
    } catch (error) {
      console.error("Error checking location:", error);
      setError(`Error checking location: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Add this function to toggle business expansion
  const toggleBusinessDetails = (businessId) => {
    if (expandedBusinessId === businessId) {
      setExpandedBusinessId(null);
    } else {
      setExpandedBusinessId(businessId);
    }
  };

  const handleOpenBusiness = (businessId) => {
    // Navigate to business detail page or open modal with details
    console.log(`Opening business with ID: ${businessId}`);
    // You can add navigation here, e.g.:
    // history.push(`/business/${businessId}`);
  };

  if (isLoading) {
    return <div className="loading">Loading businesses...</div>;
  }

  if (error && businesses.length === 0) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="directory-container">
      {/* Sidebar with navigation */}
      <div className="sidebar">
        <Link to="/" className="sidebar-link">Home</Link>
        <Link to="/chat" className="sidebar-link">Chat</Link>
        <img src={logo} alt="Oatmeal AI" className="logo" />
      </div>
      
      {/* Main content area */}
      <div className="main-content">
        {/* Header with title and image */}
        <div className="directory-header">
          <img src={getHeaderImage()} alt={formatTitle(directoryType)} className="directory-image" />
          <h1>{formatTitle(directoryType)}</h1>
        </div>
        
        {/* Location filters */}
        <div className="filter-container">
          <div className="filter-group">
            <label htmlFor="country">Country:</label>
            <select 
              id="country" 
              value={selectedCountry} 
              onChange={(e) => setSelectedCountry(e.target.value)}
              disabled={isLoading}
            >
              <option value="">Select Country</option>
              {countries.map((country, index) => (
                <option key={index} value={country}>{country}</option>
              ))}
            </select>
          </div>
          
          <div className="filter-group">
            <label htmlFor="state">State/Province:</label>
            <select 
              id="state" 
              value={selectedState} 
              onChange={(e) => setSelectedState(e.target.value)}
              disabled={isLoading || !selectedCountry}
            >
              <option value="">Select State</option>
              {states.map((state, index) => (
                <option key={index} value={state}>{state}</option>
              ))}
            </select>
          </div>
          
          <div className="filter-actions">
            <button onClick={clearAll} disabled={isLoading}>Clear</button>
            <button onClick={debugLocation} disabled={isLoading || !selectedCountry || !selectedState}>
              Check Location
            </button>
          </div>
        </div>
        
        {/* Results section */}
        <div className="results-section">
          <h2 className="results-header">Result: {businesses.length} Businesses</h2>
          
          {error && <div className="error-message">{error}</div>}
          
          {isLoading ? (
            <div className="loading">Loading businesses...</div>
          ) : (
            <div className="business-grid">
              {businesses.length > 0 ? (
                businesses.map(business => (
                  <div key={business.id} className="business-card">
                    <div className="business-name">{business.name || "Business Name"}</div>
                    <div className="business-actions">
                      <button 
                        className="open-button"
                        onClick={() => handleOpenBusiness(business.id)}
                      >
                        Open
                      </button>
                      <button 
                        className="detail-button"
                        onClick={() => toggleBusinessDetails(business.id)}
                      >
                        Detail
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-results">
                  <p>No businesses found with the current filters. Try changing your selection or clearing filters.</p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default DirectoryDetail;
