import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import '../styles/Home.css';

function Home() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is authenticated
    const token = localStorage.getItem('token');
    const justLoggedIn = sessionStorage.getItem('justLoggedIn');
    
    console.log('Home - Auth check:', { 
      hasToken: !!token, 
      justLoggedIn: !!justLoggedIn 
    });
    
    if (token) {
      setIsAuthenticated(true);
      
      // If user just logged in, redirect to directories page
      if (justLoggedIn) {
        console.log('User just logged in, will redirect to directories');
        // Clear the justLoggedIn flag
        sessionStorage.removeItem('justLoggedIn');
      }
    }
    
    setLoading(false);
  }, []);
  
  if (loading) {
    return <div className="loading">Loading...</div>;
  }
  
  // If authenticated, redirect to directories
  if (isAuthenticated) {
    console.log('User is authenticated, redirecting to directories');
    return <Navigate to="/directories" />;
  }

  return (
    <div className="home-container">
      <div className="welcome-content">
        <h1>Welcome to Oatmeal AI</h1>
        <p>Please log in to access your directories.</p>
        <div className="home-buttons">
          <a href="/login" className="home-button primary">Login</a>
          <a href="/signup" className="home-button secondary">Sign Up</a>
        </div>
      </div>
    </div>
  );
}

export default Home;
