import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import '../styles/Navbar.css';

function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  // Check authentication status on mount and when user changes
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    
    console.log('Navbar: Checking auth state');
    console.log('Navbar: Token exists:', !!token);
    console.log('Navbar: User from context:', user);
    console.log('Navbar: Stored user exists:', !!storedUser);
    
    // Consider authenticated if token exists or user exists in context
    if (token || user) {
      setIsLoggedIn(true);
      
      // Set user info from context or local storage
      if (user) {
        setUserInfo(user);
      } else if (storedUser) {
        try {
          setUserInfo(JSON.parse(storedUser));
        } catch (e) {
          console.error('Failed to parse stored user:', e);
        }
      }
    } else {
      setIsLoggedIn(false);
      setUserInfo(null);
    }
  }, [user]);

  const handleLogout = () => {
    logout();
    setIsLoggedIn(false);
    setUserInfo(null);
    navigate('/login');
  };

  // Generate initials from user's name
  const getInitials = (userObj) => {
    if (!userObj) return '?';
    
    // If fullName exists, get initials from it
    if (userObj.fullName) {
      const names = userObj.fullName.split(' ');
      if (names.length === 1) return names[0][0]?.toUpperCase() || '?';
      return (names[0][0] + names[names.length - 1][0]).toUpperCase();
    }
    
    // Fallback to email
    return userObj.email ? userObj.email[0].toUpperCase() : '?';
  };

  return (
    <nav className="navbar">
      <div className="logo">
        <Link to="/">OatmealAI</Link>
      </div>
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        {isLoggedIn ? (
          <>
            <li><Link to="/chat">Chat</Link></li>
            <li className="dropdown">
              <div className="profile-circle">
                {getInitials(userInfo)}
              </div>
              <div className="dropdown-content">
                <div className="dropdown-header">
                  <p>{userInfo?.fullName || userInfo?.email || 'User'}</p>
                  <small>{userInfo?.email || ''}</small>
                </div>
                <Link to="/profile">My Profile</Link>
                <Link to="/settings">Settings</Link>
                <button onClick={handleLogout} className="logout-dropdown-btn">Logout</button>
              </div>
            </li>
          </>
        ) : (
          <>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/signup">Signup</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
