import axios from 'axios';

// Create an axios instance with the base URL
const api = axios.create({
  baseURL: 'http://localhost:8000', // Updated to correct backend URL
  headers: {
    'Content-Type': 'application/json',
  }
});

// Request interceptor - adds auth token if available
api.interceptors.request.use(
  (config) => {
    // Get token from localStorage
    const token = localStorage.getItem('token');
    
    // Django REST framework typically expects 'Token' format rather than 'Bearer'
    if (token) {
      console.log(`API Request: Using token for ${config.url}`);
      
      // Try with 'Token' prefix (Django REST Framework default)
      config.headers.Authorization = `Token ${token}`;
      
      // Debugging log
      console.log('Authorization header:', config.headers.Authorization.substring(0, 10) + '...');
    } else {
      console.log('API Request: No token found');
    }
    
    return config;
  },
  (error) => {
    console.error('Request interceptor error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor - handles auth errors
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    // Handle 401 Unauthorized errors
    if (error.response && error.response.status === 401) {
      console.error('401 Unauthorized error from API:', {
        url: error.config.url,
        method: error.config.method,
        headers: error.config.headers.Authorization ? 
          error.config.headers.Authorization.substring(0, 15) + '...' : 'none'
      });
    }
    
    return Promise.reject(error);
  }
);

// Auth functions
export const loginUser = async (credentials) => {
  try {
    console.log('API: Sending login request with:', credentials.email);
    const response = await api.post('/api/auth/login/', credentials);
    console.log('API: Login response status:', response.status);
    return response.data;
  } catch (error) {
    console.error('API: Login error:', error.message);
    if (error.response) {
      console.error('API: Server response:', error.response.data);
    }
    throw new Error(error.response?.data?.message || 'Login failed. Please try again.');
  }
};

export const signupUser = async (userData) => {
  try {
    const response = await api.post('/api/auth/signup/', userData);
    return response.data;
  } catch (error) {
    console.error('API: Signup error:', error.message);
    throw new Error(error.response?.data?.message || 'Signup failed. Please try again.');
  }
};

// User profile
export const getUserProfile = async () => {
  // Use fetch API directly for better control
  try {
    const token = localStorage.getItem('token');
    
    if (!token) {
      console.error('API: Cannot fetch profile - No token available');
      throw new Error('Authentication required');
    }
    
    console.log('API: Fetching user profile with token');
    
    // Try with 'Token' prefix (Django REST Framework default)
    const headers = {
      'Authorization': `Token ${token}`,
      'Content-Type': 'application/json'
    };
    
    console.log('Profile request headers:', {
      Authorization: headers.Authorization.substring(0, 15) + '...'
    });
    
    // Updated URL to match the backend path
    const response = await fetch('http://localhost:8000/api/auth/profile/', {
      method: 'GET',
      headers: headers
    });
    
    // Log response status
    console.log('API: Profile fetch response status:', response.status);
    
    if (!response.ok) {
      console.error('API: Profile fetch failed:', response.status, response.statusText);
      throw new Error(`Failed to fetch profile: ${response.statusText}`);
    }
    
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('API: Error fetching user profile:', error.message);
    throw error;
  }
};

// Directories
export const getDirectories = async () => {
  try {
    console.log('API: Fetching directories');
    
    // Log the current token for debugging
    const token = localStorage.getItem('token');
    console.log('Using token for directories (first few chars):', 
      token ? token.substring(0, 10) + '...' : 'no token');
    
    // Updated URL to match the backend path
    const response = await api.get('/api/directories/');
    console.log('API: Directories fetch successful');
    return response.data;
  } catch (error) {
    console.error('API: Error fetching directories:', error.message);
    
    // Return mock data if real data fetch fails
    console.log('Returning mock directory data');
    return [
      { id: 1, name: 'Documents', path: '/documents', color: '#4285F4' },
      { id: 2, name: 'Images', path: '/images', color: '#34A853' },
      { id: 3, name: 'Projects', path: '/projects', color: '#FBBC05' },
      { id: 4, name: 'Templates', path: '/templates', color: '#EA4335' }
    ];
  }
};

export default api;
