import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/DirectoryDetail.css';
import logo from '../images/OatmealaLogoi (1).png';
import { API_ENDPOINTS } from '../config';

// Import images with the correct file extensions
import agriAssociaImg from '../images/agri_associa.png';
import artisianImg from '../images/artisian.jpg';
import brImg from '../images/br.jpg';
import crafOrgImg from '../images/craf_org.jpg';
import farmersMarketImg from '../images/farmers_market.jpg';
import farmsRanchesImg from '../images/farms_ranches.jpg';
import fiberImg from '../images/fiber.jpg';
import fiberMillsImg from '../images/fiber_mills.jpg';
import fisheriesImg from '../images/fisheries.jpeg';
import fishermenImg from '../images/fishermen.jpeg';
import foodCopImg from '../images/food_cop.jpg';
import foodHubImg from '../images/food_hub.jpg';
import groceryStoreImg from '../images/grocery_store.jpeg';
import manfacImg from '../images/manfac.jpeg';
import marinasImg from '../images/marinas.jpeg';
import meatImg from '../images/meat.jpg';
import realEstateImg from '../images/real_estate.webp';
import restaurantsImg from '../images/restaurants.jpg';
import retailersImg from '../images/retailers.png';
import serviceProvidersImg from '../images/service_providers.webp';
import universitiesImg from '../images/universities.jpeg';
import vetImg from '../images/vet.webp';
import vineyardsImg from '../images/vineyards.jpeg';
import wineriesImg from '../images/wineries.png';
import othersImg from '../images/others.jpg';

function DirectoryDetail() {
  const { directoryType } = useParams();
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [businesses, setBusinesses] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedState, setSelectedState] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Format the directory type for display (convert path slugs to title)
  const formatTitle = (slug) => {
    if (!slug) return '';
    return slug.split('-')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  const title = formatTitle(directoryType);

  // Map of directory type slugs to their corresponding images
  const directoryImages = {
    'agricultural-associations': agriAssociaImg,
    'artisan-producers': artisianImg,
    'business-resources': brImg,
    'crafter-organizations': crafOrgImg,
    'farmers-markets': farmersMarketImg,
    'farms-ranches': farmsRanchesImg,
    'fiber-cooperatives': fiberImg,
    'fiber-mills': fiberMillsImg,
    'fisheries': fisheriesImg,
    'fishermen': fishermenImg,
    'food-cooperatives': foodCopImg,
    'food-hubs': foodHubImg,
    'grocery-stores': groceryStoreImg,
    'manufacturers': manfacImg,
    'marinas': marinasImg,
    'meat-wholesalers': meatImg,
    'real-estate-agents': realEstateImg,
    'restaurants': restaurantsImg,
    'retailers': retailersImg,
    'service-providers': serviceProvidersImg,
    'universities': universitiesImg,
    'veterinarians': vetImg,
    'vineyards': vineyardsImg,
    'wineries': wineriesImg,
    'others': othersImg
  };

  // Get the correct image for the current directory type
  const getHeaderImage = () => {
    return directoryImages[directoryType] || othersImg; // Default to "others" if not found
  };

  // Load countries from API
  useEffect(() => {
    const loadCountries = async () => {
      setIsLoading(true);
      setError(null);
      try {
        console.log("Fetching countries from API...");
        const response = await fetch(`${API_ENDPOINTS.countries}`);
        console.log("Countries API response status:", response.status);
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        console.log("Countries data received:", data);
        
        if (Array.isArray(data)) {
          setCountries(data);
        } else {
          console.error("Unexpected countries data format:", data);
          setError("Received invalid data format for countries");
        }
      } catch (error) {
        console.error("Error loading countries:", error);
        setError(`Failed to load countries: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadCountries();
  }, []);

  // Load states when country is selected
  useEffect(() => {
    if (!selectedCountry) {
      setStates([]);
      return;
    }

    const loadStates = async () => {
      try {
        setIsLoading(true);
        const encodedCountry = encodeURIComponent(selectedCountry);
        
        const response = await fetch(`${API_ENDPOINTS.states}?country=${encodedCountry}`);
        if (!response.ok) {
          throw new Error('Failed to fetch states');
        }
        
        const data = await response.json();
        setStates(data);
      } catch (error) {
        console.error('Error loading states:', error);
        setError('Failed to load states. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    loadStates();
  }, [selectedCountry]);

  // Load businesses when country and state are selected
  useEffect(() => {
    if (!selectedCountry || !selectedState) {
      setBusinesses([]);
      return;
    }

    const loadBusinesses = async () => {
      try {
        setIsLoading(true);
        setError(null);
        
        const encodedTitle = encodeURIComponent(directoryType);
        const encodedCountry = encodeURIComponent(selectedCountry);
        const encodedState = encodeURIComponent(selectedState);
        
        // Use the directoryBusinesses endpoint from the config
        const apiUrl = `${API_ENDPOINTS.directoryBusinesses}?directory=${encodedTitle}&country=${encodedCountry}&state=${encodedState}`;
        console.log("Fetching businesses from:", apiUrl);
        
        const response = await fetch(apiUrl);
        
        if (!response.ok) {
          const errorData = await response.text();
          console.error("Businesses API error:", errorData);
          throw new Error(`Failed to fetch businesses: ${response.status} ${response.statusText}`);
        }
        
        const data = await response.json();
        console.log("Businesses data:", data);
        
        // Check if the response is in the expected format
        if (data.success && Array.isArray(data.businesses)) {
          setBusinesses(data.businesses);
        } else if (Array.isArray(data)) {
          setBusinesses(data); // Fallback for direct array response
        } else {
          setBusinesses([]);
          console.warn('Unexpected response format:', data);
          if (data.error) {
            setError(`Failed to load businesses: ${data.error}`);
          } else {
            setError('Received unexpected data format from server.');
          }
        }
      } catch (error) {
        console.error('Error loading businesses:', error);
        setError(`Failed to load businesses: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadBusinesses();
  }, [selectedCountry, selectedState, directoryType]);

  // Clear all selections
  const clearAll = () => {
    setSelectedCountry('');
    setSelectedState('');
    setBusinesses([]);
  };

  // Debug location
  const debugLocation = async () => {
    if (!selectedCountry || !selectedState) {
      alert('Please select both country and state first');
      return;
    }

    try {
      setIsLoading(true);
      const response = await fetch(`/api/location-info/${encodeURIComponent(selectedCountry)}/${encodeURIComponent(selectedState)}`);
      if (!response.ok) {
        throw new Error('Failed to fetch location info');
      }
      
      const data = await response.json();
      
      let message = `Location: ${selectedCountry}, ${selectedState}\n`;
      message += `Total businesses: ${data.total_businesses}\n\n`;
      message += 'Business types available in this location:\n';
      
      for (const [type, count] of Object.entries(data.business_types)) {
        message += `- ${type}: ${count} businesses\n`;
      }
      
      alert(message);
    } catch (error) {
      console.error('Error checking location:', error);
      alert('Failed to check location data');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <nav className="sidebar">
        <img src={logo} alt="Oatmeal AI Logo" className="sidebar-logo" />
        <h2>Oatmeal AI</h2>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/chat">Chat</Link></li>
        </ul>
      </nav>
      
      <main>
        <header>
          <img 
            src={getHeaderImage()} 
            alt={`${title}`} 
            className="header-image" 
          />
          <h1>{title}</h1>
        </header>
        
        <div className="filter-section">
          <div className="filter-group">
            <label>Country</label>
            <select 
              value={selectedCountry}
              onChange={(e) => setSelectedCountry(e.target.value)}
            >
              <option value="">Select Country</option>
              {countries.map((country, index) => (
                <option key={index} value={country}>{country}</option>
              ))}
            </select>
          </div>
          
          <div className="filter-group">
            <label>State</label>
            <select 
              value={selectedState}
              onChange={(e) => setSelectedState(e.target.value)}
              disabled={!selectedCountry}
            >
              <option value="">Select State</option>
              {states.map((state, index) => (
                <option key={index} value={state}>{state}</option>
              ))}
            </select>
          </div>
          
          <button className="clear-btn" onClick={clearAll}>Clear all</button>
          <button 
            className="debug-btn" 
            onClick={debugLocation}
          >
            Debug Location
          </button>
        </div>
        
        <section className="candidates">
          <h2>Result: <span id="resultCount">{businesses.length}</span> Businesses</h2>
          <div id="business-list">
            {isLoading ? (
              <div className="loading">Loading...</div>
            ) : error ? (
              <div className="error-message">{error}</div>
            ) : businesses.length === 0 && (selectedCountry && selectedState) ? (
              <div className="error-message">No businesses found for this location. Please try a different selection.</div>
            ) : (
              businesses.map((business, index) => (
                <div className="candidate" key={business.business_id || index}>
                  <div className="candidate-info">
                    <h3>{business.business_name || business.name || (typeof business === 'string' ? business : 'Unnamed Business')}</h3>
                    {business.description && <p className="business-description">{business.description}</p>}
                    <div className="business-contact-info">
                      {business.address && <p className="business-address">{business.address}</p>}
                      {business.city && business.state && (
                        <p className="business-location">
                          {business.city}, {business.state} {business.zip_code && business.zip_code}
                        </p>
                      )}
                      {business.phone && <p className="business-phone">Phone: {business.phone}</p>}
                      {business.email && <p className="business-email">Email: {business.email}</p>}
                      {business.website && <p className="business-website">Website: <a href={business.website.startsWith('http') ? business.website : `https://${business.website}`} target="_blank" rel="noopener noreferrer">{business.website}</a></p>}
                    </div>
                  </div>
                  <div className="candidate-actions">
                    <button 
                      className="open-btn" 
                      onClick={() => {
                        const businessId = business.business_id || index;
                        window.open(`/business/${businessId}`, '_blank');
                      }}
                    >
                      Open
                    </button>
                    <button className="detail-btn">Detail</button>
                  </div>
                </div>
              ))
            )}
          </div>
        </section>
      </main>
    </div>
  );
}

export default DirectoryDetail;
