import axios from 'axios';
import credentials from 'Agent-Backend-main/Agent-Backend-main/backend/azure_credentials.json'; // Adjust this path as necessary

const { api_key, endpoint, api_version } = credentials;

export const sendChatMessageToAzure = async (message) => {
  try {
    const response = await axios.post(`${endpoint}/v1/chat/completions`, {
      prompt: message,
      max_tokens: 150, // Adjust as needed
      temperature: 0.7, // Adjust as needed
    }, {
      headers: {
        'Content-Type': 'application/json',
        'api-key': api_key,
      },
    });

    return response.data; // Return the response data
  } catch (error) {
    console.error("Error sending message to Azure:", error);
    throw error; // Rethrow the error for handling in the component
  }
};

// Define getChatHistory
export const getChatHistory = async () => {
  // Implement the logic to fetch chat history from your backend
  // For example:
  const response = await axios.get(`${endpoint}/v1/chat/history`, {
    headers: {
      'api-key': api_key,
    },
  });
  return response.data; // Return the chat history
};
