import React, { useState, useContext, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { loginUser, getUserProfile } from '../services/api';
import axios from 'axios';
import '../styles/Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [backendStatus, setBackendStatus] = useState(null);
  const [testingBackend, setTestingBackend] = useState(false);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  // Test backend connectivity
  const testBackendConnection = async () => {
    setTestingBackend(true);
    setBackendStatus(null);
    
    // List of possible health check endpoints
    const testEndpoints = [
      
      'http://127.0.0.1:8000/',
    ];
    
    let connected = false;
    
    for (const endpoint of testEndpoints) {
      try {
        console.log(`Testing backend connection to: ${endpoint}`);
        const response = await axios.get(endpoint, { timeout: 5000 });
        
        if (response.status >= 200 && response.status < 500) {
          console.log(`Server responded at ${endpoint} with status: ${response.status}`);
          connected = true;
          setBackendStatus({
            connected: true,
            message: 'Backend server is online.',
            endpoint: endpoint
          });
          break;
        }
      } catch (error) {
        // For 404 responses, the server is running but endpoint doesn't exist
        if (error.response && error.response.status === 404) {
          console.log(`Server responded with 404 at ${endpoint} - server is running!`);
          connected = true;
          setBackendStatus({
            connected: true,
            message: 'Backend server is online (404 response).',
            endpoint: endpoint
          });
          break;
        }
        console.log(`Failed to connect to ${endpoint}:`, 
          error.response ? error.response.status : error.message);
      }
    }
    
    if (!connected) {
      console.error('Backend connection test failed - no working endpoints');
      setBackendStatus({
        connected: false,
        message: 'Cannot connect to backend server. Please ensure the server is running.',
        error: 'All connection attempts failed'
      });
    }
    
    setTestingBackend(false);
  };

  // Check backend on component mount
  useEffect(() => {
    testBackendConnection();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    try {
      console.log('Attempting login with email:', email);
      
      // Store email in sessionStorage for use in profile fallback
      sessionStorage.setItem('lastLoginEmail', email);
      
      // Extract username from email to use as fallback name
      const username = email.split('@')[0];
      if (username) {
        // Store capitalized username as fallback
        const displayName = username.charAt(0).toUpperCase() + username.slice(1);
        sessionStorage.setItem('userName', displayName);
      }
      
      // Use the loginUser function from api.js
      const response = await loginUser({ email, password });
      console.log('Login response received:', JSON.stringify(response));
      
      if (!response || !response.token) {
        console.error('Invalid login response:', response);
        throw new Error('Invalid login response - no token received');
      }
      
      // Store token immediately to ensure it's available for subsequent requests
      localStorage.setItem('token', response.token);
      console.log('Token stored in localStorage');
      
      // Immediate profile fetch to ensure we have user data before redirect
      try {
        console.log('Fetching user profile...');
        const userProfile = await getUserProfile();
        console.log('User profile fetched:', userProfile);
        
        // Update session storage with profile details for better UX
        if (userProfile) {
          if (userProfile.name) {
            sessionStorage.setItem('userName', userProfile.name);
          } else if (userProfile.username) {
            sessionStorage.setItem('userName', userProfile.username);
          }
          
          if (userProfile.email) {
            sessionStorage.setItem('lastLoginEmail', userProfile.email);
          }
        }
      } catch (profileError) {
        console.warn('Could not fetch profile, will use fallback data:', profileError);
        // Continue with login - we already set fallback profile data
      }
      
      // Ensure we pass email to the login function
      const loginData = {
        ...response,
        email: email // Add email to login data
      };
      
      // Use the login function from AuthContext
      await login(loginData);
      
      console.log('Login successful, redirecting to directories');
      
      // Small timeout to ensure all state updates have been processed
      setTimeout(() => {
        // Use navigate instead of direct window location for React Router compatibility
        navigate('/directories');
      }, 100);
      
    } catch (err) {
      console.error('Login error:', err);
      
      // Improved error handling with specific messages
      if (err.response && err.response.status === 401) {
        // Extract error message from response if available
        const errorMsg = err.response.data?.detail || 
                         err.response.data?.message || 
                         'Invalid email or password';
        setError(errorMsg);
        
        // Clear password field for security
        setPassword('');
      } else if (err.message === 'Network Error') {
        setError('Cannot connect to server. Please check your internet connection.');
      } else {
        setError(err.message || 'Login failed. Please check your credentials.');
      }
    } finally {
      setLoading(false);
    }
  };
  
  // Check if already logged in
  React.useEffect(() => {
    // Check token to see if already logged in
    const token = localStorage.getItem('token');
    if (token) {
      console.log('Token found in localStorage, user already logged in');
      // If already logged in, use navigate instead of window.location
      navigate('/directories');
    }
  }, [navigate]);

  return (
    <div className="login-container">
      <div className="login-form-wrapper">
        <h2>Login to Your Account</h2>
        
        {/* Show backend status */}
        {backendStatus && (
          <div className={`server-status ${backendStatus.connected ? 'success' : 'error'}`}>
            <p>{backendStatus.message}</p>
            {backendStatus.connected && backendStatus.endpoint && (
              <p className="endpoint-info">Connected to: {backendStatus.endpoint}</p>
            )}
            {!backendStatus.connected && (
              <div className="connection-help">
                <p><strong>Server Connection Help:</strong></p>
                <ul>
                  <li>Ensure Django server is running at http://127.0.0.1:8000</li>
                  <li>Check your terminal where you ran the Django server</li>
                  <li>Make sure no firewall or security software is blocking the connection</li>
                  <li>The server should show: "Starting development server at http://127.0.0.1:8000/"</li>
                </ul>
                <button 
                  onClick={testBackendConnection}
                  disabled={testingBackend}
                  className="retry-button"
                >
                  {testingBackend ? 'Testing...' : 'Retry Connection'}
                </button>
              </div>
            )}
          </div>
        )}
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          
          <button 
            type="submit" 
            className="login-button" 
            disabled={loading || (backendStatus && !backendStatus.connected)}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        
        <div className="login-footer">
          <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
          
          {error && error.includes('Invalid credentials') && (
            <div className="login-help">
              <p><strong>Login Troubleshooting:</strong></p>
              <ul>
                <li>Check if you're using the correct email and password</li>
                <li>Ensure the database has been properly set up with user accounts</li>
                <li>If using a test account, check with your administrator for the correct credentials</li>
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Login;
